INSERT INTO `event_navi_comment` VALUES (/*comment_id*/56003, /*where_type*/56, /*character_id*/190804, /*face_type*/2, /*character_name*/"カリン", /*description*/"お疲れ様でした\n討伐証の交換、\nお忘れないように！", /*voice_id*/3, /*start_time*/"2019/12/31 12:00:00", /*end_time*/"2030/01/01 14:59:59", /*pos_x*/-363.0, /*pos_y*/0.0, /*change_face_time*/1.539, /*change_face_type*/1, /*event_id*/0);
