INSERT INTO `room_unit_comments` VALUES (/*id*/3178, /*unit_id*/109901, /*trigger*/1, /*voice_id*/1, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", /*insert_word_type*/0);
