INSERT INTO `room_unit_comments` VALUES (/*id*/3187, /*unit_id*/109901, /*trigger*/2, /*voice_id*/3, /*beloved_step*/1, /*time*/0, /*face_id*/1, /*description*/"ねえ、ここで\n歌の練習って、\nしてもいいかな？", /*insert_word_type*/0);
