INSERT INTO `room_unit_comments` VALUES (/*id*/3190, /*unit_id*/109901, /*trigger*/2, /*voice_id*/102, /*beloved_step*/8, /*time*/0, /*face_id*/1, /*description*/"せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", /*insert_word_type*/0);
