INSERT INTO `room_unit_comments` VALUES (/*id*/3192, /*unit_id*/109901, /*trigger*/2, /*voice_id*/2, /*beloved_step*/1, /*time*/2, /*face_id*/1, /*description*/"お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", /*insert_word_type*/0);
