INSERT INTO `unit_comments` VALUES (/*id*/1025008, /*unit_id*/102531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"怪我とかしていません？\n回復魔法なら得意なので♪\nそれ以外の魔法は…それなりに");
INSERT INTO `unit_comments` VALUES (/*id*/1025009, /*unit_id*/102531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あっ！　またやっちゃいました～\nあなたの前では失敗しないように\n気を付けていたのに～っ");
INSERT INTO `unit_comments` VALUES (/*id*/1025010, /*unit_id*/102531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お帰りなさいませ\nご主人さま\nな、な～んて、えへへ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1025011, /*unit_id*/102501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.7, /*change_face*/1, /*description*/"お誕生日、おめでとうございます\nケーキをご用意しました！\nこ、転ばないように、\nそーーーっと持っていきますね");
INSERT INTO `unit_comments` VALUES (/*id*/1025012, /*unit_id*/102531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます♪\n今日だけは普通の女の子として\nあなたのお祝いをしちゃ、\nダメ…ですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1026001, /*unit_id*/102601, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/5.4, /*change_face*/1, /*description*/"できればずーっと\nゴロゴロしてたい…\nねえ、あんたもそう思わない？");
INSERT INTO `unit_comments` VALUES (/*id*/1026002, /*unit_id*/102601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/6.8, /*change_face*/5, /*description*/"ねーねー、あたしと一緒に\nのんびりゲームでもしようよ～\nたまには休むのも大事だよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1026003, /*unit_id*/102601, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.5, /*change_face*/5, /*description*/"あたし、お腹ぺこぺこだよ\nあんた何か持ってない？\n美味しいあんパンでもあれば\n最高なんだけどね～…");
INSERT INTO `unit_comments` VALUES (/*id*/1026004, /*unit_id*/102601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.8, /*change_face*/2, /*description*/"一人でだらだらしてることが\n多かったからさ、\nあんたっていう遊び相手が\nできて結構嬉しいんだー");
INSERT INTO `unit_comments` VALUES (/*id*/1026005, /*unit_id*/102601, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/9.5, /*change_face*/2, /*description*/"…あたしみたいなタイプってさ、\n自分の領域に人が入ってくるの\n少し苦手だったりするんだけど、\nあんたは不思議と嫌じゃないや");
INSERT INTO `unit_comments` VALUES (/*id*/1026006, /*unit_id*/102631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大好きなドングリの中で\nごろごろできるなんて…\nなんだい？\nここは天国かい？");
INSERT INTO `unit_comments` VALUES (/*id*/1026007, /*unit_id*/102631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ああ、まさにこの空間は、\nあたしのために\nあるような場所だね…\n戻ってこれなくなりそうだよ");
INSERT INTO `unit_comments` VALUES (/*id*/1026008, /*unit_id*/102631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ふかふかのベッドもいいけど\n自然に包まれてだらけるのも\nまた乙なんだよねぇ…");
INSERT INTO `unit_comments` VALUES (/*id*/1026009, /*unit_id*/102631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ほらほら、あんたもこっちに来て\n一緒にこのパラダイスを\n堪能しようじゃないか…\nあたしの隣、あいてるよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1026010, /*unit_id*/102631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな素敵な場所に\n連れてきてくれてありがとね\nこのためならいくらでも\n外にでるし、また連れてきておくれ");
INSERT INTO `unit_comments` VALUES (/*id*/1026011, /*unit_id*/102601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/2, /*description*/"誕生日おめでと～\nプレゼントは買い忘れてないよ～\n通販の時間指定を使ってるから、\nもうすぐ届くはずだよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1026012, /*unit_id*/102631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでと～\n特別な日だからこそ、\nぐうたら過ごすに限るよね\nあたしも一緒に付き合うよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1027001, /*unit_id*/102701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"あなた様のことを思って\nまごころをこめて\n媚薬づくりをしていますわ");
INSERT INTO `unit_comments` VALUES (/*id*/1027002, /*unit_id*/102701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"私の本業は花屋ですわ\nブーケなども作っています\n最近は毒草アレンジが人気です");
INSERT INTO `unit_comments` VALUES (/*id*/1027003, /*unit_id*/102701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/9.0, /*change_face*/6, /*description*/"花は嫌いではありません…\n生活の糧ですし薬の材料にも\nなりますから。…美しさ？\nそれは考えたこともありません");
INSERT INTO `unit_comments` VALUES (/*id*/1027004, /*unit_id*/102701, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/5.5, /*change_face*/5, /*description*/"こうして研究をしていると\n心が弾みます…\n薬が完成すればあなた様を\n自由にできると思うと…");
INSERT INTO `unit_comments` VALUES (/*id*/1027005, /*unit_id*/102701, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/"花占いでもしましょうか\nあなた様は私のことが、好き…\n大好き…好き…大好き…");
INSERT INTO `unit_comments` VALUES (/*id*/1027006, /*unit_id*/102731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どのような女性が好みですか？\n明るい私？　クールな私？\nもの静かな私？　…もちろん、\n私以外の方ではありませんよね？");
INSERT INTO `unit_comments` VALUES (/*id*/1027007, /*unit_id*/102731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"森の魔物…人の心を読み取り\n好みの幻を見せると聞きますわ\nあなた様がその魔物に遭ったら\nどのような幻を見るのでしょうね");
INSERT INTO `unit_comments` VALUES (/*id*/1027008, /*unit_id*/102731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私だけを見てください\n今目の前にいる私だけを\nたとえ別の私でも、\n目移りするのは許しませんよ");
INSERT INTO `unit_comments` VALUES (/*id*/1027009, /*unit_id*/102731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もしもあなた様の偽物が現れたら\nいっそ縛り上げて観賞用に…\nいえ、偽物は所詮偽物ですわ\n私に必要なのはあなた様だけ…");
INSERT INTO `unit_comments` VALUES (/*id*/1027010, /*unit_id*/102731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな私をお望みですか？\nあなた様が望むなら、\nその通りに私は変わります\n愛していますから…");
INSERT INTO `unit_comments` VALUES (/*id*/1027011, /*unit_id*/102701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.2, /*change_face*/5, /*description*/"お誕生日を祝して、バースデー\nケーキをつくりましたわ\n隠し味は、睡眠薬としびれ薬と\n惚れ薬と媚薬と…私の愛情です");
INSERT INTO `unit_comments` VALUES (/*id*/1027012, /*unit_id*/102731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nプレゼントを用意しましたわ\n婚姻届と、結婚指輪と、\n二人の新居です");
INSERT INTO `unit_comments` VALUES (/*id*/1028001, /*unit_id*/102801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"クレヨンと絵本と積み木が\nいっぺんに買えて\nしかも安い店を見つけたの！\n選ぶのを手伝ってくれない？");
INSERT INTO `unit_comments` VALUES (/*id*/1028002, /*unit_id*/102801, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"料理をする時は、\n食材の隅から隅まで\n使い切らないとね\nもったいないじゃない！");
INSERT INTO `unit_comments` VALUES (/*id*/1028003, /*unit_id*/102801, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.0, /*change_face*/2, /*description*/"お金も大事だけど、あたしはただ\nみんなの笑顔が見たいの\nもちろん、その中には\nあんたも入ってるんだからね");
INSERT INTO `unit_comments` VALUES (/*id*/1028004, /*unit_id*/102801, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/"あんたと今度、\nどこかに遊びに行きたいわね\nあ、もちろん、\nお金のかからないところによ？");
INSERT INTO `unit_comments` VALUES (/*id*/1028005, /*unit_id*/102801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/2.0, /*change_face*/2, /*description*/"お茶会を開こうと思うの\nもちろん、\nあんたも招待してあげるから\n楽しみにしてなさい！");
INSERT INTO `unit_comments` VALUES (/*id*/1028006, /*unit_id*/102831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"限定アイテムの列はこちらが\n最後尾です…あら、奇遇ね\nあんたも寄ってかない？\nあたしたちが主催するバザー！");
INSERT INTO `unit_comments` VALUES (/*id*/1028007, /*unit_id*/102831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしがお客さん呼び込むから、\nあんたはこの看板持って\n誘導してくれる？");
INSERT INTO `unit_comments` VALUES (/*id*/1028008, /*unit_id*/102831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"予想以上にお客さんが来てくれて\nビックリだわ！　せっかくだし\nあんたも寄ってきなさいよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1028009, /*unit_id*/102831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"整列手伝ってくれてありがと♪\nあんたと一緒だといつもより\n何倍も仕事が楽しいわ♪\nまたお願いね！");
INSERT INTO `unit_comments` VALUES (/*id*/1028010, /*unit_id*/102831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"バザーは大盛況よ♪\nこのあとパーッとご飯でも\n行きましょ！　あんたがいれば\n疲れなんか吹っ飛んじゃう♪");
INSERT INTO `unit_comments` VALUES (/*id*/1028011, /*unit_id*/102801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.1, /*change_face*/2, /*description*/"ハッピーバースデー♪\n救護院のみんながプレゼントを\n選んだわ。もちろんあたしもね\nありがたく受け取りなさい！");
INSERT INTO `unit_comments` VALUES (/*id*/1028012, /*unit_id*/102831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日だったわね？\nお祝いに遊びに行くわよ\nこんな日くらいはしっかり休んで\nたまには楽しみましょ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1029001, /*unit_id*/102901, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"私、今のポジションには\n満足してないんだ！\nトップアイドルめざして\n歌も踊りも磨かないとね！");
INSERT INTO `unit_comments` VALUES (/*id*/1029002, /*unit_id*/102901, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/6.0, /*change_face*/5, /*description*/"…私の写真集みてるの？　この\n派手な水着は撮影用だってば！\nプライベートで着るやつは\nもっと可愛いもん。…見たい？");
INSERT INTO `unit_comments` VALUES (/*id*/1029003, /*unit_id*/102901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/3.6, /*change_face*/2, /*description*/"トップアイドルへの道は\nまだまだ遠いなあ…\nダンスと歌のレッスン、\nもうちょっと頑張ろうっと！");
INSERT INTO `unit_comments` VALUES (/*id*/1029004, /*unit_id*/102901, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.4, /*change_face*/2, /*description*/"歌も踊りも\nもっともっと上手になりたいな！\nキミには、一番輝いてる私を\n見てほしいから♪");
INSERT INTO `unit_comments` VALUES (/*id*/1029005, /*unit_id*/102901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/2.3, /*change_face*/5, /*description*/"アイドルのお勉強は進んでる？\nでも…私以外のアイドルの\nことはあまり勉強しなくても\nいいからね！");
INSERT INTO `unit_comments` VALUES (/*id*/1029006, /*unit_id*/102931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"トップアイドルになれたら\n世界はどんな風に見えるんだろ？\nもしそうなった時も\nキミは隣にいてくれるかな…？");
INSERT INTO `unit_comments` VALUES (/*id*/1029007, /*unit_id*/102931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ステージに立つからには\nチャレンジする気持ちを\n忘れたらダメだよね！\nこれからも前向きにいくよー！");
INSERT INTO `unit_comments` VALUES (/*id*/1029008, /*unit_id*/102931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、がんばってトップアイドルの\n高みを目指すから！\n応援、よろしくね！");
INSERT INTO `unit_comments` VALUES (/*id*/1029009, /*unit_id*/102931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが望むなら\nアイドルやめてもいいかなって\n思ったこともあったんだよ？\n…ちょっとだけね！");
INSERT INTO `unit_comments` VALUES (/*id*/1029010, /*unit_id*/102931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この歌、片思いの女の子の心が\nよく出てるねって？\n…ふふふ、キミって鈍感♪");
INSERT INTO `unit_comments` VALUES (/*id*/1029011, /*unit_id*/102901, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/1.2, /*change_face*/1, /*description*/"おめでとう♪　キミの誕生日を\nお祝いできるように、\nずっと前からスケジュールを\n調整してたんだ！　うれしい？");
INSERT INTO `unit_comments` VALUES (/*id*/1029012, /*unit_id*/102931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー、トゥユー♪\nハッピーバースデー、トゥユー♪\nどう？　キミだけのための\nプライベートライブだよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1030001, /*unit_id*/103001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"デケデケデン！\nこのニノン、ショーグンのために\n忠義を尽くすデース！\n見ててクダサイ！");
INSERT INTO `unit_comments` VALUES (/*id*/1030002, /*unit_id*/103001, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/4.0, /*change_face*/2, /*description*/"一大事デス！\nアクダイカンが現れたデス！\n一緒に成敗するでゴザルデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1030003, /*unit_id*/103001, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"あ、ショーグン！\nそこでなにを…ハッ！？\nもしや忍法の修行デスか！？\nニノンもやるデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1030004, /*unit_id*/103001, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"ショーグンのためなら、\nたとえ火の中、水の中デス！\nなぜなら…ワタシの命は、\nショーグンのものだからデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1030005, /*unit_id*/103001, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.5, /*change_face*/5, /*description*/"ヤマトナデシコ…\nニノンの国にも伝わる言葉デス\nワタシがそうなったら、\nショーグンもメロメロデスよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1030006, /*unit_id*/103031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか…\nショーグンと共に\n天下統一するデス！\nそれがニノンの夢なのデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1030007, /*unit_id*/103031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ニノンに切れぬものはないデス！\nショーグンはヤカタブネに\n乗ったつもりで\n安心していてクダサーイ！");
INSERT INTO `unit_comments` VALUES (/*id*/1030008, /*unit_id*/103031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ニノンが、ショーグンのゾーリ\nふところで\nあっためておいたデス！\n一緒にでかけマショ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1030009, /*unit_id*/103031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグン、手をつなぐデース！\nえへへ…\nこれでニノンとショーグンは、\n固い絆で結ばれたデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1030010, /*unit_id*/103031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグン！　ご飯にするデス？\nお風呂にするデス？\nそれとも…ワ・タ・シ…？\nは、恥ずかしいデス…");
INSERT INTO `unit_comments` VALUES (/*id*/1030011, /*unit_id*/103001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/2, /*description*/"誕生日おめでとうデス！\nお祝いの紅白マンジェウを\n用意したデス！　二人羽織で\n食べさせてあげるデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1030012, /*unit_id*/103031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンのお祝いするデス！\n誕生日は、おジャシキ遊びで\nシュラシュシュシュデース！\nどんちゃん騒ぎデース！");
INSERT INTO `unit_comments` VALUES (/*id*/1031001, /*unit_id*/103101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.5, /*change_face*/2, /*description*/"今日の運勢は最悪です\nえっと、気にしないでください\nちなみに…私の占いは、\nよく当たります");
INSERT INTO `unit_comments` VALUES (/*id*/1031002, /*unit_id*/103101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.5, /*change_face*/4, /*description*/"あなたの後ろの人、\n誰ですか…？\nえ？　見えない？");
INSERT INTO `unit_comments` VALUES (/*id*/1031003, /*unit_id*/103101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/7.0, /*change_face*/2, /*description*/"タロットカード、星占い、\n四柱推命…いろいろ\nありますが、私のおススメは、\n父を使った水晶占いです");
INSERT INTO `unit_comments` VALUES (/*id*/1031004, /*unit_id*/103101, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.0, /*change_face*/5, /*description*/"父はあなたを\n誤解しているようです\nとてもいい人なのに…");
INSERT INTO `unit_comments` VALUES (/*id*/1031005, /*unit_id*/103101, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/5, /*description*/"今日のあなたには、\n女難の相がでていますね\nあまり私以外の女性とは、\n話をしない方が…");
INSERT INTO `unit_comments` VALUES (/*id*/1031006, /*unit_id*/103131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"舞がきれい…ですか\nうれしい\n修練した甲斐がありました");
INSERT INTO `unit_comments` VALUES (/*id*/1031007, /*unit_id*/103131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これは祓魔の舞です\nまだ練習中なので\nあまり見ないでください\n…恥ずかしいですから");
INSERT INTO `unit_comments` VALUES (/*id*/1031008, /*unit_id*/103131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"父がこの舞を見るたびに\n成仏しそうだと褒めてくれます\n父に成仏されては\n困るのですが…");
INSERT INTO `unit_comments` VALUES (/*id*/1031009, /*unit_id*/103131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたに褒めてもらえると\nいつもより舞がうまくできます\nプラスの気は\n霊を祓うのに最適なんです");
INSERT INTO `unit_comments` VALUES (/*id*/1031010, /*unit_id*/103131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"生きている人で舞を見せるのは\nあなたが初めてです\n少し恥ずかしいですけど\n…あなたに見てほしいです");
INSERT INTO `unit_comments` VALUES (/*id*/1031011, /*unit_id*/103101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/10.3, /*change_face*/5, /*description*/"あなたが歳を取って、\nおじいさんになったら、一緒に\n私もおばあさんになりますね\nもちろん、幽霊になったら…");
INSERT INTO `unit_comments` VALUES (/*id*/1031012, /*unit_id*/103131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n今日は、とてもいい運勢ですよ\n金運とか健康運とか…\nその…れ、恋愛運とか…");
INSERT INTO `unit_comments` VALUES (/*id*/1032001, /*unit_id*/103201, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.0, /*change_face*/1, /*description*/"困ったことがあれば、\n何でも申してくださいな！\n私がなんとかして\n差し上げますわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1032002, /*unit_id*/103201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.0, /*change_face*/1, /*description*/"ごきげんよう\n私が一人前の商人になるため、\nご指導ご鞭撻のほど\nよろしくお願いしますわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1032003, /*unit_id*/103201, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.5, /*change_face*/5, /*description*/"私は確かに方向音痴ですが、\nそこまで酷くありませんわ\n…ところで、私の家は\nどちらかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1032004, /*unit_id*/103201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"あなたさまが私の騎士になる日を\n私はいつまでも待っていますわ\nその時には、二人で\n色々な場所に行きますわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1032005, /*unit_id*/103201, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"お金で買えないものも\nあるのですわね…\n例えば、あなたさまの\n心…ですわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1032006, /*unit_id*/103231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"『一人前の商人とは\n一人前の武人でもある！』\nウィスタリア家の教えを\n今こそ守りますわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1032007, /*unit_id*/103231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この程度の相手、\n私の敵ではありません\n手早く片付けて、\n勝利の祝宴と参りますわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1032008, /*unit_id*/103231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の商売を邪魔するやからは\nなんぴとたりとも許しません！\n成敗してさしあげます！");
INSERT INTO `unit_comments` VALUES (/*id*/1032009, /*unit_id*/103231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたさまが\nそばにいてくださることが、\nどれほど私を強くしているか、\nお気づきかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1032010, /*unit_id*/103231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたさまと一緒なら、\n私は迷わずに、\nどこまでもまっすぐ進みますわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1032011, /*unit_id*/103201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"今日はあなたさまの誕生日？\nまあ、おめでとうございます！\nすぐにパーティーを\n開きましょう！");
INSERT INTO `unit_comments` VALUES (/*id*/1032012, /*unit_id*/103231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日、おめでとうございます\nあなたさまの誕生日の\n価値の前では、どんな宝石も\nかすんで見えてしまいますわね！");
INSERT INTO `unit_comments` VALUES (/*id*/1033001, /*unit_id*/103301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"お笑いの道は奥が深けぇべ！\nオラと一緒に、\n笑いの道、極めるべ！");
INSERT INTO `unit_comments` VALUES (/*id*/1033002, /*unit_id*/103301, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/4.0, /*change_face*/4, /*description*/"オラが一番おっかねぇのは、\n敵に負けることじゃねえ\nネタがすべることだ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1033003, /*unit_id*/103301, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.0, /*change_face*/1, /*description*/"あんちゃん、この牛乳ウマいべ？\nお乳を出したのは、ウマじゃなくて\n牛だけどな！");
INSERT INTO `unit_comments` VALUES (/*id*/1033004, /*unit_id*/103301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"あんちゃん、オラと一緒に、\nお笑い界の頂点めざすべ！\nめざせ、お笑いチャンピオン！");
INSERT INTO `unit_comments` VALUES (/*id*/1033005, /*unit_id*/103301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"あんちゃん、オラと一緒に\nネタ、作るべ！\nもう二時間だけ「寝た」後に…\nなんちゃって！");
INSERT INTO `unit_comments` VALUES (/*id*/1033006, /*unit_id*/103331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ほーい、集まってけれ～！\nエリザベスパーク印の\n牛乳、ヨーグルト、チーズ！\n直売だべよ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1033007, /*unit_id*/103331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"街の衆、味見してけろ！\n出来立てだぁ、ほんとに旨ぇぞ～\nオラ嘘はつかねえよ、\n冗談は言うけどな！");
INSERT INTO `unit_comments` VALUES (/*id*/1033008, /*unit_id*/103331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"街のみんながこんなに\n応援してくれてんだ！\n絶対に爆笑をかっさらうべよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1033009, /*unit_id*/103331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"オラのギャグ、おもしろいべか？\nなんつってもあんちゃんは、\nオラの初めてのファンだ\nきっとオラたち波長が合うだよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1033010, /*unit_id*/103331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"な、なあ…あんちゃんのこと\n相方って呼んでもいいべか？\nそれがダメなら婿さんに…\nあ、逆だ！　こっぱずかしー！");
INSERT INTO `unit_comments` VALUES (/*id*/1033011, /*unit_id*/103301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.7, /*change_face*/2, /*description*/"ひょうたん、常備してっか？\nなにキョトンとしてんだべ？\n今日誕生日のあんちゃんのための\nギャグだべ！　おめでとう～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1033012, /*unit_id*/103331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデーだべ！\n今日はあんちゃんのためだけに\n渾身ネタを、飛ばーすでー！\nいっぱい笑ってほしいべよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1034001, /*unit_id*/103401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"何か困ったことがあったら\n相談してね\nいつでも力になるから\nお姉さんとの約束よ？");
INSERT INTO `unit_comments` VALUES (/*id*/1034002, /*unit_id*/103401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.5, /*change_face*/2, /*description*/"キミ、いま暇だったりしない？\nだったら私と一緒に、\nどこか飲みにいきましょ？\nもちろんキミはジュースで、ね");
INSERT INTO `unit_comments` VALUES (/*id*/1034003, /*unit_id*/103401, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/5.0, /*change_face*/5, /*description*/"仕事持って帰ってきたけど\n終わりそうにないよ～！\nうぅ…キミ、\n少し手伝ってくれない？");
INSERT INTO `unit_comments` VALUES (/*id*/1034004, /*unit_id*/103401, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/7.5, /*change_face*/2, /*description*/"えへへ～…\nキミって暖かいんらね～\nちょっと飲みすぎらから、\nキミで暖を取らせて～");
INSERT INTO `unit_comments` VALUES (/*id*/1034005, /*unit_id*/103401, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"キミ、ご飯はちゃんと食べてる？\nあ、良かったらお姉さんが、\n作ってあげようか？");
INSERT INTO `unit_comments` VALUES (/*id*/1034006, /*unit_id*/103431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"はぁ…このベリー、いい香り♪\n飲み物にしたらさぞ美味しい…\nいけないいけない！\n仕事中なのに！");
INSERT INTO `unit_comments` VALUES (/*id*/1034007, /*unit_id*/103431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"だめよユカリ…！\nこのままじゃいつもみたいに\nダメなところを見せちゃうわ！");
INSERT INTO `unit_comments` VALUES (/*id*/1034008, /*unit_id*/103431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"な～んか、ちょっとだけ\nいい気分になってきたかも…\n気のせい…かなぁ？");
INSERT INTO `unit_comments` VALUES (/*id*/1034009, /*unit_id*/103431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミが大人になった時のために\n飲み物、一本作っておこうか？\nその日がきたら、二人で一緒に\nお祝いしながら飲みましょう");
INSERT INTO `unit_comments` VALUES (/*id*/1034010, /*unit_id*/103431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇ…今度、一緒に\nデートしない？\nそれとも、私みたいなのが\n相手じゃ、いやかな…？");
INSERT INTO `unit_comments` VALUES (/*id*/1034011, /*unit_id*/103401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.0, /*change_face*/1, /*description*/"誕生日おめでとう！\n今日はいっぱい\nお祝いしてあげるね！");
INSERT INTO `unit_comments` VALUES (/*id*/1034012, /*unit_id*/103431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミの誕生日というわけで\nまずは…かんぱーい！\nねえ、今日はずっと\n一緒にいてもいいかな…？");
INSERT INTO `unit_comments` VALUES (/*id*/1036001, /*unit_id*/103601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.9, /*change_face*/5, /*description*/"今は、勉強の時間です\n話しかけないでくださ…\nもう、そんな顔しないでください\n遊んであげますから！");
INSERT INTO `unit_comments` VALUES (/*id*/1036002, /*unit_id*/103601, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/4.8, /*change_face*/4, /*description*/"ピーマンとにんじんを食べないと\n大きくなれない…？\nううっ…大きくはなりたい\nですけれどぉ～っ");
INSERT INTO `unit_comments` VALUES (/*id*/1036003, /*unit_id*/103601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"聞いてください\n身長が伸びていました！\n一ミリも！");
INSERT INTO `unit_comments` VALUES (/*id*/1036004, /*unit_id*/103601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.2, /*change_face*/5, /*description*/"今は子供ですけど、\nすぐに大きくなりますから\nその時には…その…");
INSERT INTO `unit_comments` VALUES (/*id*/1036005, /*unit_id*/103601, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ふふっ。とても頑張りましたね\n今日はほめてあげます\n頭をナデナデしてあげましょう");
INSERT INTO `unit_comments` VALUES (/*id*/1036006, /*unit_id*/103631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私を守ってあげる？　\n余計なお世話です！\nで、ですが…\nありがとうございます");
INSERT INTO `unit_comments` VALUES (/*id*/1036007, /*unit_id*/103631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こ、腰が抜けちゃいました…\nは、恥ずかしい…");
INSERT INTO `unit_comments` VALUES (/*id*/1036008, /*unit_id*/103631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うぅ…どうして\nこの人がいる時に限って、\nかっこ悪くなっちゃうのかな…");
INSERT INTO `unit_comments` VALUES (/*id*/1036009, /*unit_id*/103631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ま、魔物なんてこわくないです！\nで、でもその…守ってくれて\n助かりました…");
INSERT INTO `unit_comments` VALUES (/*id*/1036010, /*unit_id*/103631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"街の外は魔物が出て危ないです\nですから、私が守ってあげますよ");
INSERT INTO `unit_comments` VALUES (/*id*/1036011, /*unit_id*/103601, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/3.0, /*change_face*/2, /*description*/"今日、お誕生日なんですよね\nおこづかいを貯めて、\nカップケーキを買ってきたので、\n召し上がってください");
INSERT INTO `unit_comments` VALUES (/*id*/1036012, /*unit_id*/103631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nどんどん大人になっちゃう…\n私も早く追いつかないと…\nな、なんでもないです！");
INSERT INTO `unit_comments` VALUES (/*id*/1037001, /*unit_id*/103701, /*use_type*/1, /*voice_id*/1, /*face_id*/3, /*change_time*/8.2, /*change_face*/1, /*description*/"おや、何をしてるのかな？\n不審な行動をしたら、\nあなたでも逮捕するよ？\nなんて、冗談だけど♪");
INSERT INTO `unit_comments` VALUES (/*id*/1037002, /*unit_id*/103701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.6, /*change_face*/2, /*description*/"稽古に付き合ってほしいな\n大丈夫大丈夫！\nきちんと手加減するから！\nふふ、多分だけどね");
INSERT INTO `unit_comments` VALUES (/*id*/1037003, /*unit_id*/103701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.6, /*change_face*/2, /*description*/"さて、今日も町の見回りに\n行かないと。あなたも来る？\n何があっても、私が守って\nあげるから安心していいよ");
INSERT INTO `unit_comments` VALUES (/*id*/1037004, /*unit_id*/103701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.4, /*change_face*/4, /*description*/"うーん、自分では器用な方だと\n思ってたんだけどなぁ…\nあなたの前だと上手くいかない\n…どうしちゃったのかなぁ");
INSERT INTO `unit_comments` VALUES (/*id*/1037005, /*unit_id*/103701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/9.1, /*change_face*/5, /*description*/"からかうつもりなのに、あなたには\nいつも逆にやられちゃう\nまったく、調子が狂って困る\nほんと…困るよ…");
INSERT INTO `unit_comments` VALUES (/*id*/1037006, /*unit_id*/103731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"相手の動きを見て、正確に急所へ\n必殺の一撃を叩きこむ！\nそれが剣術の基本だよ");
INSERT INTO `unit_comments` VALUES (/*id*/1037007, /*unit_id*/103731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦いの後ってすごく疲れるし、\nお腹が空くんだ…あなたも\n一緒にご飯なんてどうかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1037008, /*unit_id*/103731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"無駄な動きを削っていけば、\n剣技は磨き上げられる\n単純だけど難しいんだよね…");
INSERT INTO `unit_comments` VALUES (/*id*/1037009, /*unit_id*/103731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたを守るためにも、もっと\n強くなりたい。最近は\nそう思うようになったよ\n…剣をやっていてよかった");
INSERT INTO `unit_comments` VALUES (/*id*/1037010, /*unit_id*/103731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一緒にいるだけで力が\n湧いてくる。あなたの存在が\n私を強くしてくれるんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1037011, /*unit_id*/103701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.3, /*change_face*/2, /*description*/"ふ～ん、今日が誕生日なんだ？\nじゃあ、大人になったあなたに\nご飯でもおごってもらおうかな？\nな～んて、冗談だよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1037012, /*unit_id*/103731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！　ん、急に\n押しかけて迷惑だった？　今日は\nあなたの部屋を片付けようと思って\nまるでお嫁さんみたいじゃない？　");
INSERT INTO `unit_comments` VALUES (/*id*/1038001, /*unit_id*/103801, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/2.5, /*change_face*/5, /*description*/"おすすめの本があるんです\nお互いに感想をいいあえたら\n楽しいだろうなって…");
INSERT INTO `unit_comments` VALUES (/*id*/1038002, /*unit_id*/103801, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/2.0, /*change_face*/1, /*description*/"ケホッ、ケホッ…\nあの、背中を\nさすってもらえませんか？");
INSERT INTO `unit_comments` VALUES (/*id*/1038003, /*unit_id*/103801, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/7.0, /*change_face*/5, /*description*/"もう少し身体がよくなったら、\n一緒に、どこか遠くへ…\n…なんて。えへへ");
INSERT INTO `unit_comments` VALUES (/*id*/1038004, /*unit_id*/103801, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.5, /*change_face*/5, /*description*/"体力がないから\nすぐに疲れちゃって…\n私、迷惑かけてませんか？");
INSERT INTO `unit_comments` VALUES (/*id*/1038005, /*unit_id*/103801, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.5, /*change_face*/4, /*description*/"頼ってばかりではいけませんね\n私も自分のことは自分で…\nごほごほっ…！\nすみません…お水をください");
INSERT INTO `unit_comments` VALUES (/*id*/1038006, /*unit_id*/103831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この子、ニャットちゃんって\nいいます\nとっても聞き分けの\nいい子なんですよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1038007, /*unit_id*/103831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"体が大きいけどニャットちゃん\nとっても甘えん坊なんですよ\nニャットちゃん、\nいい子でちゅね～");
INSERT INTO `unit_comments` VALUES (/*id*/1038008, /*unit_id*/103831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"子どもだったニャットちゃんも\nすっかり立派になりました\n私の、大事なお友達です");
INSERT INTO `unit_comments` VALUES (/*id*/1038009, /*unit_id*/103831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"疲れていませんか？\nよかったら…\n私と一緒に休憩しません？");
INSERT INTO `unit_comments` VALUES (/*id*/1038010, /*unit_id*/103831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ニャットちゃんに抱きつくと、\nモフモフして気持ちいいです\nあなたの傍も…気持ちいいです");
INSERT INTO `unit_comments` VALUES (/*id*/1038011, /*unit_id*/103801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.2, /*change_face*/5, /*description*/"お誕生日おめでとうございます\nあの…お姉ちゃんからも、\nお祝いされましたか？\nす、少し気になっただけで…");
INSERT INTO `unit_comments` VALUES (/*id*/1038012, /*unit_id*/103831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この本、お誕生日プレゼントです\n恋愛小説なんですけど、たまたま\nヒロインの名前がシオリで、\nしゅ、主人公の名前が…");
INSERT INTO `unit_comments` VALUES (/*id*/1040001, /*unit_id*/104001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"友達を作るためのチーム、\nそれがBB団！\nえへへ…これからも、\nよろしくお願いします！");
INSERT INTO `unit_comments` VALUES (/*id*/1040002, /*unit_id*/104001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.5, /*change_face*/5, /*description*/"友達作りの道は、\n誰でも一歩目から\n踏み出してるんですよね\n私も…踏み出したいな…");
INSERT INTO `unit_comments` VALUES (/*id*/1040003, /*unit_id*/104001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"今日も、お花に水あげて、\n動物さんにご飯をあげて～♪\nえへへ、ぼっちでも、\n楽しみはそれなりにあります");
INSERT INTO `unit_comments` VALUES (/*id*/1040004, /*unit_id*/104001, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/9.0, /*change_face*/7, /*description*/"私、たまに思っちゃうんです\nあなたがいてくれるなら、\n友達ができなくても…\nはっ、な、なんでもないです…");
INSERT INTO `unit_comments` VALUES (/*id*/1040005, /*unit_id*/104001, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/3.5, /*change_face*/6, /*description*/"あの人は、私のことを…はっ、\nいつからそこに！？　ええと、\nこれは、花占いです。ど、どんな\n占いかって…内緒ですっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1040006, /*unit_id*/104031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いまは微力な私だけど、きっと\nみなさんのお役に立ちます\nそうすれば…いつか友達、\n増えるかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1040007, /*unit_id*/104031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぼっちだとしても…\n私にもきっとできることが\nあるはずだって思ったんです\n精一杯がんばりますね！");
INSERT INTO `unit_comments` VALUES (/*id*/1040008, /*unit_id*/104031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"守りたいものを守る…\nそのためにがんばります\nBB団は私に、色々なことを\n教えてくれました！");
INSERT INTO `unit_comments` VALUES (/*id*/1040009, /*unit_id*/104031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"BB団の活動のたびに\nあなたに会えるのが楽しみで…\n毎日、早く明日がこないかなって\n思っちゃいます！");
INSERT INTO `unit_comments` VALUES (/*id*/1040010, /*unit_id*/104031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ…私にこの先、\nどれだけ友達が増えても、\n一番はあなただから…\n安心してくださいねっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1040011, /*unit_id*/104001, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/9.2, /*change_face*/2, /*description*/"お、おたたん、おたんこなすー！\nすみません、ハッピーなお祝いの\n言葉を口にしたことがなくて…\n誕生日おめでとう五郎丸ー！");
INSERT INTO `unit_comments` VALUES (/*id*/1040012, /*unit_id*/104031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n森にいる小鳥さんたちと\n合唱団を結成して、\nお祝いの歌を作ってきました！");
INSERT INTO `unit_comments` VALUES (/*id*/1042001, /*unit_id*/104201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"私の唱喚術は、人々を\n守るためのもの…この力を、\nもっと役立てたいんです");
INSERT INTO `unit_comments` VALUES (/*id*/1042002, /*unit_id*/104201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"あの…良かったらまた、\nアイドルの練習に\n付き合ってもらえませんか？");
INSERT INTO `unit_comments` VALUES (/*id*/1042003, /*unit_id*/104201, /*use_type*/1, /*voice_id*/3, /*face_id*/7, /*change_time*/3.5, /*change_face*/6, /*description*/"～～…♪\nい、いつからそこに！？\n…今の、聞いてました？");
INSERT INTO `unit_comments` VALUES (/*id*/1042004, /*unit_id*/104201, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.0, /*change_face*/5, /*description*/"あなたの前だと、\n緊張せずに歌えるんです…\nどうしてでしょう…？");
INSERT INTO `unit_comments` VALUES (/*id*/1042005, /*unit_id*/104201, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.5, /*change_face*/5, /*description*/"歌にはいろんな可能性がある…\nそれを教えてくれたのは仲間と\n…あなたです");
INSERT INTO `unit_comments` VALUES (/*id*/1042006, /*unit_id*/104231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…もう少しだけ、近くに…\nそばにいるだけで、不思議と\nメロディが浮かんでくるんです");
INSERT INTO `unit_comments` VALUES (/*id*/1042007, /*unit_id*/104231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"目が覚めました？　向こうの街で\nお祭りが始まりましたよ\n後で一緒に行ってみませんか？");
INSERT INTO `unit_comments` VALUES (/*id*/1042008, /*unit_id*/104231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"…書きとめられないくらい\n心の中から言葉が溢れてくる…\n私、こんなに歌いたかったんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1042009, /*unit_id*/104231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつか…聞いてくださいね\nあなたへの思いを綴った歌を…\n恥ずかしがらずに、歌いますから");
INSERT INTO `unit_comments` VALUES (/*id*/1042010, /*unit_id*/104231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"波の音が心地いいですね\nずっと一緒に聞いていたいくらい\n…私、今なにか言いました？");
INSERT INTO `unit_comments` VALUES (/*id*/1042011, /*unit_id*/104201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.6, /*change_face*/5, /*description*/"お誕生日おめでとうございます\n歌以外にいいプレゼントが\n思いつかなくて…\nよかったら聞いてください");
INSERT INTO `unit_comments` VALUES (/*id*/1042012, /*unit_id*/104231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\n短いものですが、お祝いに\n曲を書いてみました\nここで歌ってもいいですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1043001, /*unit_id*/104301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.3, /*change_face*/2, /*description*/"さて、パトロールにでも行くか\n一緒に行きたいなら、\nついてきてもいいぜ");
INSERT INTO `unit_comments` VALUES (/*id*/1043002, /*unit_id*/104301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/3.5, /*change_face*/2, /*description*/"おい、飯はちゃんと\n食ってんだろうな？\n食ってないなら、\nあたしが用意してやるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1043003, /*unit_id*/104301, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/6.2, /*change_face*/5, /*description*/"あたしみたいな奴、\n普通の女の子扱いするのなんて、\nおまえぐらいだぜ\nわ、わりとうれしいな");
INSERT INTO `unit_comments` VALUES (/*id*/1043004, /*unit_id*/104301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.6, /*change_face*/2, /*description*/"あたしの親友の恩人なら、\nあたしの恩人みたいなもんだ\nお互い仲良くしていこうぜ！");
INSERT INTO `unit_comments` VALUES (/*id*/1043005, /*unit_id*/104301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.1, /*change_face*/5, /*description*/"お前って、けっこう男気もあるし\n肝っ玉もすわってるよな…\n一緒にいて、あたしは楽しいよ\nお前はどうだ？");
INSERT INTO `unit_comments` VALUES (/*id*/1043006, /*unit_id*/104331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしから離れんなよな！\nどんなやつが来ようが、\nぶった切ってやる！");
INSERT INTO `unit_comments` VALUES (/*id*/1043007, /*unit_id*/104331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弱い奴らばっかで、\n暴れ足りねぇ\n歯ごたえのある奴は、\nいねぇのか！？");
INSERT INTO `unit_comments` VALUES (/*id*/1043008, /*unit_id*/104331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お前と一緒なら、\nどんな敵も楽勝だぜ！\nお前はあたしが守るからよ、\nだからあたしのことも、頼むぜ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1043009, /*unit_id*/104331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よっしゃー！\n敵が全部片付いたら、\n二人でパーッと飯だ飯！\nお前の好物たくさん作ってやる！");
INSERT INTO `unit_comments` VALUES (/*id*/1043010, /*unit_id*/104331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"力がみなぎってくるぜぇ！\nあたしから離れんなよ！\n何があっても絶対に\nお前を守ってやんよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1043011, /*unit_id*/104301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.2, /*change_face*/2, /*description*/"へぇ、今日が誕生日だったのか\nおめでとさん！\nんじゃ、せっかくだし\nなんかおごってやるよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1043012, /*unit_id*/104331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日ってお前の誕生日だよな？\nぐ、偶然、たまたま作った\nケーキがあるんだけど…\n食わねぇか？");
INSERT INTO `unit_comments` VALUES (/*id*/1044001, /*unit_id*/104401, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.0, /*change_face*/1, /*description*/"お主…わらわと何処かで\n会ったことはないか？\nお主を見ていると、何故か\nなつかしさを覚えるのじゃが…");
INSERT INTO `unit_comments` VALUES (/*id*/1044002, /*unit_id*/104401, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/6.4, /*change_face*/1, /*description*/"むう…どういうわけか\n無性にトマトジュースが\n飲みたい気分じゃの…\nお主、どこぞで求めてまいれ");
INSERT INTO `unit_comments` VALUES (/*id*/1044003, /*unit_id*/104401, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/6.9, /*change_face*/2, /*description*/"昼間は思うように動けぬ…\n夜になるまで、じっとしておこう\nそれまでお主は、わらわの\n話し相手になるのじゃぞ");
INSERT INTO `unit_comments` VALUES (/*id*/1044004, /*unit_id*/104401, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/4.2, /*change_face*/4, /*description*/"お主はちっとも、わらわを\n恐れないのじゃな？\nな？　可愛らしいじゃと？\n眷属の分際で何を申すー！");
INSERT INTO `unit_comments` VALUES (/*id*/1044005, /*unit_id*/104401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/9.0, /*change_face*/5, /*description*/"お主は、特別な眷属じゃ\n今より「昼を総べる者」を\n名乗るが良いぞ");
INSERT INTO `unit_comments` VALUES (/*id*/1044006, /*unit_id*/104431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわの真の姿は美しかろう？\nなに？　見慣れてないから\nいつもの方が良い？　なんと\n張り合いのないことを…！");
INSERT INTO `unit_comments` VALUES (/*id*/1044007, /*unit_id*/104431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわの野望を聞きたいのか？\nもちろん世界征服じゃ！\n女と生まれたからには\n一度は目指したいものよの♪");
INSERT INTO `unit_comments` VALUES (/*id*/1044008, /*unit_id*/104431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"わらわは伝説の吸血鬼\nお主の命を奪うなど\nたやすいことじゃが…まあ、\n眷属ゆえ生かしておいてやろうぞ");
INSERT INTO `unit_comments` VALUES (/*id*/1044009, /*unit_id*/104431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"世界を征服した暁には\nその半分をお主に分け与えよう\nど、どういう意味かわかるな？");
INSERT INTO `unit_comments` VALUES (/*id*/1044010, /*unit_id*/104431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"昼間にお主と会うために、\n最近は昼夜逆転してしまった…\n昼ふかしは、美容によくないの\nじゃが…");
INSERT INTO `unit_comments` VALUES (/*id*/1044011, /*unit_id*/104401, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/12.9, /*change_face*/6, /*description*/"今日は誕生日か？　めでたいな！\nいくつになったのじゃ？\nそうか。わらわがその歳の頃は…\nはて？　何をしていたかのう？");
INSERT INTO `unit_comments` VALUES (/*id*/1044012, /*unit_id*/104431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"眷属の誕生日を祝して、\nわらわの膝枕を贈呈しようぞ\nどうじゃ、心地よいじゃろう？\n腕枕でも胸枕でも良いのじゃぞ？");
INSERT INTO `unit_comments` VALUES (/*id*/1045001, /*unit_id*/104501, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/4.0, /*change_face*/2, /*description*/"ぐふふふ…\nえ？　な、何でもないですよ？\nあなたを見て、妄想なんて…\nはぁはぁ…");
INSERT INTO `unit_comments` VALUES (/*id*/1045002, /*unit_id*/104501, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/5.0, /*change_face*/6, /*description*/"「ドＳさんは、クウカを\n追いつめるとムリヤリ…」\nでゅふふふふ…あっ！\nいいいつの間に居たのですか！？");
INSERT INTO `unit_comments` VALUES (/*id*/1045003, /*unit_id*/104501, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.3, /*change_face*/6, /*description*/"この間読んだ本が、\nとっても素敵で…え？\n内容ですか？　そ、それは…\n聞かないほうがいいですよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1045004, /*unit_id*/104501, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.0, /*change_face*/5, /*description*/"夢の中にまでドＳさんが\n出てきてくれました…\n寝ても覚めても、クウカは\n支配され続けてしまうのですね");
INSERT INTO `unit_comments` VALUES (/*id*/1045005, /*unit_id*/104501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.9, /*change_face*/5, /*description*/"ドＳさんと二人っきり…\nこ、これは…！？\nクウカはどんな辱めを\n受けてしまうのでしょう…！");
INSERT INTO `unit_comments` VALUES (/*id*/1045006, /*unit_id*/104531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな汚れたクウカを見る\nドSさんの冷ややかな視線\n…ふひっ、ふひひ！\nああ、また昂ぶってしまいます！");
INSERT INTO `unit_comments` VALUES (/*id*/1045007, /*unit_id*/104531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな醜態をさらしながらも\nこの後さらにドSさんに\nめちゃくちゃにされることを\nクウカは望んでいますぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1045008, /*unit_id*/104531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"「魔物どもにこんなにされて\n嬉しいんだろええおい！」なんて\n言葉責めするつもりですね！？\nさすがはドＳさん、上級者…！");
INSERT INTO `unit_comments` VALUES (/*id*/1045009, /*unit_id*/104531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"もうお嫁にいけません…\nだから、一生ドSさんの\n支配下に置いてください！\nもっと体に教え込んでください！");
INSERT INTO `unit_comments` VALUES (/*id*/1045010, /*unit_id*/104531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"クウカはもうドＳさんなしでは\n生きられない体に…\nこ、この責任はちゃんと\n取ってくれるのですよね？");
INSERT INTO `unit_comments` VALUES (/*id*/1045011, /*unit_id*/104501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.5, /*change_face*/2, /*description*/"誕生日おめでとうございます\nケーキに深く刺さるロウソクが、\nとろっとろに溶けていますよ\nフゥー…って消してください");
INSERT INTO `unit_comments` VALUES (/*id*/1045012, /*unit_id*/104531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ドＳさんの誕生日ですね…\nクウカの体に巻いたリボンを\n乱暴にほどいて、包装紙を破る\n子供のように服も…ぐふふふふ");
INSERT INTO `unit_comments` VALUES (/*id*/1046001, /*unit_id*/104601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"野良猫たちに、\nたい焼きを買ってあげてるにゃ！\nみんな喜んでくれるんだにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1046002, /*unit_id*/104601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.3, /*change_face*/2, /*description*/"早く準備して出かけるにゃ！\n「時はルピなり」だにゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1046003, /*unit_id*/104601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.8, /*change_face*/2, /*description*/"お客さ～ん♪\nここだけのオイシイ話が\nあるんだにゃ～\n一口乗ってみないかにゃ～？");
INSERT INTO `unit_comments` VALUES (/*id*/1046004, /*unit_id*/104601, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/4.9, /*change_face*/2, /*description*/"あたしは気まぐれだから、\nちゃーんとつかまえて\nほしいにゃ？\nでないと逃げちゃうにゃ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1046005, /*unit_id*/104601, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/6.0, /*change_face*/2, /*description*/"ふふふ\nキミが怪我したら…\nぺろぺろ舐めてあげにゃくも…\nないかにゃ～？");
INSERT INTO `unit_comments` VALUES (/*id*/1046006, /*unit_id*/104631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"これでも、困ってる人がいたら\n助けずにはいられないのにゃ！\n今夜は正義のため、猫と一緒に\n飛び回るのにゃー！");
INSERT INTO `unit_comments` VALUES (/*id*/1046007, /*unit_id*/104631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今度、ギルドにも\n遊びに来るといいにゃ\nにゃふふ、これで人手が増え…\n…にゃ、にゃんでもないにゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1046008, /*unit_id*/104631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あたしは気まぐれだけど、\nやる時はやる猫なのにゃ\n今日はやるかにゃ？\nだったら…全力にゃ！");
INSERT INTO `unit_comments` VALUES (/*id*/1046009, /*unit_id*/104631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"えへへ。キミといられるなら…\n家猫になっちゃっても\nいいかにゃ～\nにゃんてにゃ？");
INSERT INTO `unit_comments` VALUES (/*id*/1046010, /*unit_id*/104631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あとで、キミと二人で\n食べようと思って\nたい焼きを取ってあるのにゃ\n半分こして食べようにゃ～");
INSERT INTO `unit_comments` VALUES (/*id*/1046011, /*unit_id*/104601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"今日はキミの誕生日みたいにゃ！\nだから今日だけ特別に\nたい焼き食べ放題に\nしてあげるにゃ～");
INSERT INTO `unit_comments` VALUES (/*id*/1046012, /*unit_id*/104631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日、おめでとうにゃ！\n気まぐれなあたしでも、\n今日ぐらいは…キミだけの\n猫になってあげてもいいにゃ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1047001, /*unit_id*/104701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こう見えて私は女なんだ\nわけあって鎧は外せないが\nよろしく頼むよ");
INSERT INTO `unit_comments` VALUES (/*id*/1047002, /*unit_id*/104701, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"鎧に太陽光を浴びせると\n表面が恐ろしく熱くなるが\n中の私は意外と平気なんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1047003, /*unit_id*/104701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"体を動かすのは好きだが\n水泳は苦手なんだ\n鎧が重くてね…");
INSERT INTO `unit_comments` VALUES (/*id*/1047004, /*unit_id*/104701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"顔を見せられない分\n今なにをどう思っているか\n伝えるよう努力するよ");
INSERT INTO `unit_comments` VALUES (/*id*/1047005, /*unit_id*/104701, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私の表情は見えないかも\nしれないが、君の表情は\nしっかり見えているよ、いつもね");
INSERT INTO `unit_comments` VALUES (/*id*/1047006, /*unit_id*/104731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この剣を振らずに済む\n世の中にするためにも、\n私は剣を振らねばならないんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1047007, /*unit_id*/104731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんな強敵が相手であろうと\n私は恐れずに立ち向かうよ\n子供たちの笑顔のために");
INSERT INTO `unit_comments` VALUES (/*id*/1047008, /*unit_id*/104731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"たとえ炎に包まれた戦場でも\n助けを呼ぶ声がある限り\n私はその地へと赴こう");
INSERT INTO `unit_comments` VALUES (/*id*/1047009, /*unit_id*/104731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私が戦っている時\n君の視線を背中に感じると\n心が安らぐんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1047010, /*unit_id*/104731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は団長として人々の安息は\n守らねばならないが、君だけは\n優先して守ってしまいそうだ…");
INSERT INTO `unit_comments` VALUES (/*id*/1047011, /*unit_id*/104701, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は君の誕生日なんだってね\n一つ大人になったんだから、今年は\nあまり無茶をしないでくれよ？\n君のことが心配なんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1047012, /*unit_id*/104731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！　何か\nお願い事はないかい？　そ、それは\nダメだ少年！　兜を脱ぐなんて…\n恥ずかしくて爆発してしまうよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1048001, /*unit_id*/104801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.9, /*change_face*/2, /*description*/"素材を稼ぐか、ルピを稼ぐか…\nどっちの戦略も練ってあるから\n必要なら相談してね");
INSERT INTO `unit_comments` VALUES (/*id*/1048002, /*unit_id*/104801, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/2.9, /*change_face*/2, /*description*/"今回用意した計画もバッチリよ\nもし街の外に行くのなら\n早めに出かけましょう");
INSERT INTO `unit_comments` VALUES (/*id*/1048003, /*unit_id*/104801, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/4.5, /*change_face*/1, /*description*/"効率的な動きに大事なのは、\n観察する力よ\n普段からちゃんと鍛えてる？");
INSERT INTO `unit_comments` VALUES (/*id*/1048004, /*unit_id*/104801, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.1, /*change_face*/5, /*description*/"かけ持ちしすぎちゃったから\nバイトを手伝ってくれない？\nほら、キミなら頼りになるし");
INSERT INTO `unit_comments` VALUES (/*id*/1048005, /*unit_id*/104801, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.8, /*change_face*/2, /*description*/"キミのスケジュール管理、\n私がしてあげようか？\n24時間つきっきりで\nもちろん管理費はいただくけど");
INSERT INTO `unit_comments` VALUES (/*id*/1048006, /*unit_id*/104831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"よければキミのお財布、\n私が管理してあげよっか？\nえっ？　盗らないわよっ！\n見損なわないでっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1048007, /*unit_id*/104831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この町に泥棒が現れたの！？\nまじめに働いてる人への\n冒涜じゃない！\nゆ、許せないわっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1048008, /*unit_id*/104831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は特売セールの日！\nもちろんキミも、一緒にきてね！\n油断したら命に関わるわよ！\nそれじゃあ…とっつげき～！");
INSERT INTO `unit_comments` VALUES (/*id*/1048009, /*unit_id*/104831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あの…キミって、\n好きなタイプっているのかしら？\nえ？　ちょ、ちょっと気になった\nだけよ。それだけだってばっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1048010, /*unit_id*/104831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミを傷つけるやつがいたら…\nこうしてやっつけてあげるわ！\n私の大切な弟子だもの！");
INSERT INTO `unit_comments` VALUES (/*id*/1048011, /*unit_id*/104801, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"ケーキの準備よし！\nプレゼントもよし！\nさあ、効率的な\n誕生日パーティーを始めるわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1048012, /*unit_id*/104831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう\nお祝いの言葉なんて\n非効率的かもしれないけど…\nキミにはちゃんと言いたかったの");
INSERT INTO `unit_comments` VALUES (/*id*/1049001, /*unit_id*/104901, /*use_type*/1, /*voice_id*/1, /*face_id*/5, /*change_time*/4.8, /*change_face*/2, /*description*/"なんでもかんでもどんなことでも\nお姉ちゃんに言ってね！\nもっともっと頼って甘えて～\n遠慮しないでいいんだぞっ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1049002, /*unit_id*/104901, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/3.2, /*change_face*/2, /*description*/"いつでもお姉ちゃんに\n甘えてくれていいんだぞっ☆\nナデナデだって、ギューだって、\nいっぱいしてあげちゃうからね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1049003, /*unit_id*/104901, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/4.3, /*change_face*/2, /*description*/"お姉ちゃん、うれしくって、\nついチョップが出ちゃいそう～\nこれはお姉ちゃんの愛情だから、\nしっかり受け止めてね！");
INSERT INTO `unit_comments` VALUES (/*id*/1049004, /*unit_id*/104901, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/2.8, /*change_face*/2, /*description*/"色んなこと、全部やってあげる♪\nだって、弟くんのお世話をするのは\nお姉ちゃんの役目だもん♪");
INSERT INTO `unit_comments` VALUES (/*id*/1049005, /*unit_id*/104901, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/2.1, /*change_face*/2, /*description*/"なになに？　用事？\nいつでも、どこでも、\nどんなことでも、\nお姉ちゃん、大歓迎だよ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1049006, /*unit_id*/104931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"可愛い弟くんを困らせる人は、\nお姉ちゃんが\nぜーんぶ成敗してあげる！");
INSERT INTO `unit_comments` VALUES (/*id*/1049007, /*unit_id*/104931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どんなことがあっても、\n弟くんはお姉ちゃんが\n絶対守るからね！\n弟くんには指一本触れさせない！");
INSERT INTO `unit_comments` VALUES (/*id*/1049008, /*unit_id*/104931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お姉ちゃんは、弟くんを\n守るために強くなったんだもん\nお姉ちゃんは、弟くんの盾\n弟くんの剣。弟くんの鎧だよ");
INSERT INTO `unit_comments` VALUES (/*id*/1049009, /*unit_id*/104931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんとのひとときを邪魔する\n野暮な奴は許さないよ！\nチョップくらいじゃ\n済まさないんだから！");
INSERT INTO `unit_comments` VALUES (/*id*/1049010, /*unit_id*/104931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"弟くんをいじめるなんて、\n神さまが認めても、\n天が見逃しても、\nお姉ちゃんが許さない！");
INSERT INTO `unit_comments` VALUES (/*id*/1049011, /*unit_id*/104901, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/7.9, /*change_face*/2, /*description*/"キミが一つ歳をとっても、\nお姉ちゃんも歳をとってるから、\n私はずっと弟くんのお姉ちゃんの\nまんまだよ♪　安心した？");
INSERT INTO `unit_comments` VALUES (/*id*/1049012, /*unit_id*/104931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとう！\n弟くんがどれだけ大人になったか\nお姉ちゃんに、しっかり見せて！\nじーーーーーーーーーーーーっ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1050001, /*unit_id*/105001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"ちょっとこの雑誌見てー！\n右の服と左の服、\nどっちがアタシに似合うと思う？");
INSERT INTO `unit_comments` VALUES (/*id*/1050002, /*unit_id*/105001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.9, /*change_face*/6, /*description*/"ね、ちょっと聞いていい？\nアンタって３桁の掛け算とかも\n出来たりするわけ？\nま、まさか割り算とかも…！？");
INSERT INTO `unit_comments` VALUES (/*id*/1050003, /*unit_id*/105001, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/3.5, /*change_face*/5, /*description*/"大人のレディには教養も大事？\nきょ、教養ってあれよね、\n成績とは関係ないのよね！？");
INSERT INTO `unit_comments` VALUES (/*id*/1050004, /*unit_id*/105001, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/5.5, /*change_face*/2, /*description*/"同年代の子と話しても、みんな\n子どもっぽくって退屈なのよね\nだからアンタと遊んであげるわよ\n感謝しなさいよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1050005, /*unit_id*/105001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.4, /*change_face*/5, /*description*/"まずカフェに行って、次に…\nアンタはこうゆうの、\n詳しくなさそうだから\nアタシがエスコートしないとね！");
INSERT INTO `unit_comments` VALUES (/*id*/1050006, /*unit_id*/105031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"大人なレディだもん、\n魔物退治くらい簡単よ！\nまぁ、どうしても気になるなら、\n付いてきてもいいけど？");
INSERT INTO `unit_comments` VALUES (/*id*/1050007, /*unit_id*/105031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この前、魔物を蹴散らして\nがっつりお宝ゲットしたのよ！\n「子どもに大金は必要ない」って\n親にとりあげられたけど…");
INSERT INTO `unit_comments` VALUES (/*id*/1050008, /*unit_id*/105031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"どきなさーい魔物ども♪\nレディ・ミサキのお通りよ！\nアンタはちゃんと、\nエスコートしなさいよね！");
INSERT INTO `unit_comments` VALUES (/*id*/1050009, /*unit_id*/105031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんか最近、アンタと出かける\n機会、多いわよね\nアタシはけっこう楽しんでるわよ\nアンタはどう？");
INSERT INTO `unit_comments` VALUES (/*id*/1050010, /*unit_id*/105031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"二人で魔物退治して、\nそのお金で豪華にディナー！\nそれってすっごく大人よねぇ♪\nで、デートじゃないわよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1050011, /*unit_id*/105001, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/9.8, /*change_face*/6, /*description*/"誕生日、おめでと！\nプレゼントは…ア・タ・シ・よ♪\n…あれ？　引いてる？\n雑誌に書いてあったのに…？");
INSERT INTO `unit_comments` VALUES (/*id*/1050012, /*unit_id*/105031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\nアタシが、大人なバースデーを\nプロデュースしてあげるわ\n楽しみにしてなさい♪");
INSERT INTO `unit_comments` VALUES (/*id*/1051001, /*unit_id*/105101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/3.0, /*change_face*/2, /*description*/"ねえキミ、今時間ある？\n怖がらなくても大丈夫\n危ないことはしないわ\n私を信じて…ね？");
INSERT INTO `unit_comments` VALUES (/*id*/1051002, /*unit_id*/105101, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.3, /*change_face*/5, /*description*/"良かったら一緒に実験しない？\n新しい発見があった時の\n興奮は計り知れないわよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1051003, /*unit_id*/105101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/2.5, /*change_face*/2, /*description*/"科学の発展はみんなのため\nこう見えても、病気や怪我を\n治すのが目標なの");
INSERT INTO `unit_comments` VALUES (/*id*/1051004, /*unit_id*/105101, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.4, /*change_face*/2, /*description*/"キミの体を調べている内に、\nキミ自身に興味が出て来たわ\nうふふっ…");
INSERT INTO `unit_comments` VALUES (/*id*/1051005, /*unit_id*/105101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/1.7, /*change_face*/5, /*description*/"いつも実験に協力してくれて\nありがとう。お礼に今度、\nとっておきの秘薬をあげるわ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1051006, /*unit_id*/105131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あーら、私におしおきされたいの？\nそう…目覚めちゃったのね\nたっぷりと可愛がってあげる♪");
INSERT INTO `unit_comments` VALUES (/*id*/1051007, /*unit_id*/105131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねぇ、どうかしら…？\n私を見て、何か感じない？\n恐怖？　…おかしいわね");
INSERT INTO `unit_comments` VALUES (/*id*/1051008, /*unit_id*/105131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"痛みが快楽に変わる瞬間は、\n必ず訪れるものよ…\nキミも体験してみない？");
INSERT INTO `unit_comments` VALUES (/*id*/1051009, /*unit_id*/105131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミを虜にするための香水を\nつけてきたはずなのに、\nどうして魔物ばっかり…");
INSERT INTO `unit_comments` VALUES (/*id*/1051010, /*unit_id*/105131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミといると、不思議だけど…\n鼓動がぐっと早まるの\n…確かめてみる？");
INSERT INTO `unit_comments` VALUES (/*id*/1051011, /*unit_id*/105101, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/12.8, /*change_face*/2, /*description*/"キミ、今日が誕生日でしょう？\nはいこれ。私からのプレゼント\n特別に調合した薬なの。効能？\n…飲んでからのお楽しみ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1051012, /*unit_id*/105131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー！\n私からの贈り物は…これよ\n元気が出る特製ドリンク♪\n一気に飲んじゃだめよ…？");
INSERT INTO `unit_comments` VALUES (/*id*/1052001, /*unit_id*/105201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.8, /*change_face*/5, /*description*/"私、力持ちだからそれを活かして\nお仕事もいっぱい頑張ってるの♪\nだけど、これって女子的には\nちょっとどうなのかしら～？");
INSERT INTO `unit_comments` VALUES (/*id*/1052002, /*unit_id*/105201, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/7.0, /*change_face*/9, /*description*/"どう？　すてきな鎧でしょ？\nデザインもおしゃれだし\nボディにジャストフィットなの！\n私のお気に入りよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1052003, /*unit_id*/105201, /*use_type*/1, /*voice_id*/3, /*face_id*/5, /*change_time*/0.0, /*change_face*/5, /*description*/"も～、あなたったらさっきから\nモフモフしすぎじゃない？\nせっかくブラッシングして\n毛並みを整えたばかりなのに～");
INSERT INTO `unit_comments` VALUES (/*id*/1052004, /*unit_id*/105201, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/4.8, /*change_face*/1, /*description*/"少しでも女の子らしく\n見えるように、私、がんばるわ！\nこのままあなたのそばに\n居られるように…");
INSERT INTO `unit_comments` VALUES (/*id*/1052005, /*unit_id*/105201, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/0.0, /*change_face*/5, /*description*/"はぁ～…憧れちゃう…\n普通の人間の女の子みたいに\nおしゃれしたり食べ歩きしたり\nあなたとデートしたいな…");
INSERT INTO `unit_comments` VALUES (/*id*/1052006, /*unit_id*/105231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな見た目だからって\n最初からあきらめちゃダメよね！\nあなたに嫌われたくないし\n身だしなみには気を付けるわ");
INSERT INTO `unit_comments` VALUES (/*id*/1052007, /*unit_id*/105231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なあに？　またモフモフするの？\nまあいいけどね、あなただから\nい～こい～こしてあげるわね");
INSERT INTO `unit_comments` VALUES (/*id*/1052008, /*unit_id*/105231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔物に間違われることも多いけど\n私、運命なんかに負けないわ！\nあなたと会ってから、ますます\nそう思えるようになったの");
INSERT INTO `unit_comments` VALUES (/*id*/1052009, /*unit_id*/105231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたは私のこの姿を見ても\n女の子として接してくれた！\n人の内側をきちんと見てくれる\nそんなあなたが大好き！");
INSERT INTO `unit_comments` VALUES (/*id*/1052010, /*unit_id*/105231, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんなに綺麗な空の下を\n大好きな人と一緒に歩けること\n普通の女の子になったみたいで\nすっごく嬉しいの！");
INSERT INTO `unit_comments` VALUES (/*id*/1052011, /*unit_id*/105201, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/7.2, /*change_face*/9, /*description*/"お誕生日おめでとう～！\nあなたのためにお祝いのプランを\nいっぱい考えたの♪\nやだっ、私ったらはしゃぎすぎ？");
INSERT INTO `unit_comments` VALUES (/*id*/1052012, /*unit_id*/105231, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデートゥーユー♪\n人間の男の子の誕生日をこうして\nお祝いできるなんてね…\n私がプレゼントをもらった気分♪");
INSERT INTO `unit_comments` VALUES (/*id*/1053001, /*unit_id*/105301, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"貴公と私、共に\nヴァイスフリューゲルの\n興隆のために\n力を尽くそうではないか！");
INSERT INTO `unit_comments` VALUES (/*id*/1053002, /*unit_id*/105301, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/2.7, /*change_face*/5, /*description*/"こ、子供ではない！\nた、確かに少々背は低いが…\nまだ成長期に違いない！");
INSERT INTO `unit_comments` VALUES (/*id*/1053003, /*unit_id*/105301, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"ん？　どうした？\n私と鍛錬がしたいのか？\n殊勝な心がけだな、貴公！");
INSERT INTO `unit_comments` VALUES (/*id*/1053004, /*unit_id*/105301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.5, /*change_face*/2, /*description*/"軍人は、受けた恩は返すものだ\nだから…私にできることが\nあれば、なんでも言ってくれ\n頼りにしてくれよな");
INSERT INTO `unit_comments` VALUES (/*id*/1053005, /*unit_id*/105301, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.0, /*change_face*/5, /*description*/"と、ところでだな…\n今度、貴公と二人で、\n我が本国を旅行するとか…\nど、どうだろうか？");
INSERT INTO `unit_comments` VALUES (/*id*/1053006, /*unit_id*/105331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何度言えばわかるのだ！\nだから、私は子供ではないっ！\nば、バカにするな～！\nこ、こら、頭をなでるな～！");
INSERT INTO `unit_comments` VALUES (/*id*/1053007, /*unit_id*/105331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"天空より舞い降りし\nヴァイスフリューゲルの剣技！\nその華麗なる姿を、\n見せてやる！");
INSERT INTO `unit_comments` VALUES (/*id*/1053008, /*unit_id*/105331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴公！\n気がたるんではいないか？\n今すぐ準備をして平原に来い！\n私と共に鍛錬だ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1053009, /*unit_id*/105331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は子供ではない、が…\n貴公から見たら、やはり子供に\n見えてしまうのだろうか？\nそうでないと、いいのだが…");
INSERT INTO `unit_comments` VALUES (/*id*/1053010, /*unit_id*/105331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"貴公に私の命は預けた\nこの身が尽きるまで…\n軍人の誇りを持って、\n二人で戦いぬくぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1053011, /*unit_id*/105301, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.1, /*change_face*/2, /*description*/"今日は貴公の誕生日らしいな\n大切な一日を共に祝えて、\n私も嬉しく思うぞ！\n心から祝おうじゃないか！");
INSERT INTO `unit_comments` VALUES (/*id*/1053012, /*unit_id*/105331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう\nプレゼント代わりに…\n今日は私が共に過ごしてやろう\nじ、時間を無駄にするでないぞ！");
INSERT INTO `unit_comments` VALUES (/*id*/1054001, /*unit_id*/105401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/1.9, /*change_face*/5, /*description*/"いいですか騎士さん\nレイ様に変なことしたら\nただじゃおきませんよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1054002, /*unit_id*/105401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.8, /*change_face*/2, /*description*/"騎士さんがレイ様に\n変なことしないように\n私がしっかり見張ります！");
INSERT INTO `unit_comments` VALUES (/*id*/1054003, /*unit_id*/105401, /*use_type*/1, /*voice_id*/3, /*face_id*/4, /*change_time*/2.0, /*change_face*/1, /*description*/"ああ、また服が…\nちょっとこっちに来てください\nすぐに直してあげますから");
INSERT INTO `unit_comments` VALUES (/*id*/1054004, /*unit_id*/105401, /*use_type*/1, /*voice_id*/4, /*face_id*/4, /*change_time*/4.8, /*change_face*/6, /*description*/"あの…騎士さんって\nやっぱりレイ様のこと…\nな、なんでもないです！\n忘れてください！");
INSERT INTO `unit_comments` VALUES (/*id*/1054005, /*unit_id*/105401, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/6.5, /*change_face*/6, /*description*/"どうして騎士さんは\nひどいことした私にここまで\n優しくしてくれるんですか？\nまさか…また縛られたいとか！？");
INSERT INTO `unit_comments` VALUES (/*id*/1054006, /*unit_id*/105431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何匹来ても平気です！\n騎士さんがそばにいてくれるから！\n…変な意味じゃありませんよ！？");
INSERT INTO `unit_comments` VALUES (/*id*/1054007, /*unit_id*/105431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そこはさすがに近すぎます\nもう少し下がってください\nさもないと、日頃の鬱憤\nぶつけちゃいますよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1054008, /*unit_id*/105431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"素材が余ったら騎士さんにも\n何か作ってあげましょうか\nそうですね…何がいいですか？");
INSERT INTO `unit_comments` VALUES (/*id*/1054009, /*unit_id*/105431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"最近も…相変わらずレイ様と\n仲いいんですか…？\nき、聞いてみただけですっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1054010, /*unit_id*/105431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"騎士さんは私に何か\n手伝ってほしいことあります？\n報酬は特別になしでいいです");
INSERT INTO `unit_comments` VALUES (/*id*/1054011, /*unit_id*/105401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.3, /*change_face*/5, /*description*/"た、誕生日なんですよね？\n一応おめでとうございます！\n…どこで知ったかなんて\nどうでもいいじゃないですか！");
INSERT INTO `unit_comments` VALUES (/*id*/1054012, /*unit_id*/105431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あの、おめでとうございます…\n騎士さんの誕生日ですよ！\nこれ…ハンカチです…え？\nプレゼントですっ！　バカっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1055001, /*unit_id*/105501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.5, /*change_face*/5, /*description*/"ジー…っは！\nせ、先輩！？　えっとあのその…\nな、なんでもないですぅ…");
INSERT INTO `unit_comments` VALUES (/*id*/1055002, /*unit_id*/105501, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/4.7, /*change_face*/5, /*description*/"はわわ、しぇんぴゃい！？\nほほ、ほんじちゅはお日柄もよく\nか、噛んじゃった…恥ずかしいよ");
INSERT INTO `unit_comments` VALUES (/*id*/1055003, /*unit_id*/105501, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/3.6, /*change_face*/4, /*description*/"あ、せ、先輩！？\nつ、つけてたわけじゃ…\nこ、これはその…観察で…");
INSERT INTO `unit_comments` VALUES (/*id*/1055004, /*unit_id*/105501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/5.7, /*change_face*/5, /*description*/"あ、あ、あの…せせ、先輩！\nよかったりゃ今度、一緒に…\nご飯…や、やっぱむり～！");
INSERT INTO `unit_comments` VALUES (/*id*/1055005, /*unit_id*/105501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.4, /*change_face*/5, /*description*/"せ、先輩…い、いつかちゃんと\nお礼するので…\nま、待っててください！");
INSERT INTO `unit_comments` VALUES (/*id*/1055006, /*unit_id*/105531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"魔物退治のお手伝いを\n私なんかにさせていただいて\nありがとうございます！\nが、頑張りますから！");
INSERT INTO `unit_comments` VALUES (/*id*/1055007, /*unit_id*/105531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦うのは怖いけど、\n今は先輩がいるから\nとっても心強いです！");
INSERT INTO `unit_comments` VALUES (/*id*/1055008, /*unit_id*/105531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩は下がっててください！\nわた、私が…魔物を…\nうぅ、やっぱり手伝ってくださーい");
INSERT INTO `unit_comments` VALUES (/*id*/1055009, /*unit_id*/105531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いつも助けられてばっかりだから\n…今度は私が先輩を助けます！\nお、恩返しがしたいんです！");
INSERT INTO `unit_comments` VALUES (/*id*/1055010, /*unit_id*/105531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"せ、先輩と私のコンビネーション\n…バ、バッチリですね！\nえへへへ、共同作業だぁ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1055011, /*unit_id*/105501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/8.8, /*change_face*/5, /*description*/"あっあの、先輩っ！\nお誕生日おめでとうございます！\nお祝いさせてくだひゃい！\nちゃ、ちゃんと言えましたっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1055012, /*unit_id*/105531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\nプ、プレ、プレゼントどうぞっ！\n先輩に似合う服を見つけました\nそれから次は、先輩が好きな…");
INSERT INTO `unit_comments` VALUES (/*id*/1056001, /*unit_id*/105601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/5.2, /*change_face*/2, /*description*/"お節介がすぎるのか、よく\n面倒ごとに巻き込まれるんだ\nお前さんも困ったことがあれば\n遠慮なく声かけておくれ");
INSERT INTO `unit_comments` VALUES (/*id*/1056002, /*unit_id*/105601, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/4.4, /*change_face*/1, /*description*/"ひと仕事終えたし\n釣りにでも行くとするか…\nお前さんもどうだい？");
INSERT INTO `unit_comments` VALUES (/*id*/1056003, /*unit_id*/105601, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/8.3, /*change_face*/5, /*description*/"お前さん、いい所に来たね！\n悪さしてたヤツらをちょいと\n懲らしめたら謝礼がもらえてね\nちょいと一杯付き合っとくれよ");
INSERT INTO `unit_comments` VALUES (/*id*/1056004, /*unit_id*/105601, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/2.7, /*change_face*/1, /*description*/"抱き締めるのはよせって？\n分かっちゃいるんだけどね\nお前さんがいい男なのが\n悪いんだよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1056005, /*unit_id*/105601, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.0, /*change_face*/2, /*description*/"かわいい見た目してるのに\n意外と肝が据わってるね\nまた厄介事があった時は\n頼らせておくれよ、お前さん");
