INSERT INTO `unit_comments` VALUES (/*id*/1081003, /*unit_id*/108101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.8, /*change_face*/4, /*description*/"ハロウィンの時期は\nあなたの周りも賑やかなので…\n肩が重く感じませんか？");
INSERT INTO `unit_comments` VALUES (/*id*/1081004, /*unit_id*/108101, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/6.3, /*change_face*/2, /*description*/"この格好、ハロウィンが好きな人\nみたいで恥ずかしいですけど…\n気に入ってもらえたら\n嬉しいです");
INSERT INTO `unit_comments` VALUES (/*id*/1081005, /*unit_id*/108101, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/6.9, /*change_face*/5, /*description*/"霊のみなさん、楽しそうです…\nハロウィンですからね\nわ、私も…楽しいです\nあ、あなたと、いられるので…");
INSERT INTO `unit_comments` VALUES (/*id*/1081006, /*unit_id*/108131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハロウィンのダンスパーティーが\n始まりますよ\nあなたも来てください…\n素敵な時間が待っていますから");
INSERT INTO `unit_comments` VALUES (/*id*/1081007, /*unit_id*/108131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハロウィンパーティーに、大勢の\n霊のみなさんが来てくれました…\n霊感の高まる時期ですから、\nあなたにも見えるでしょうか？");
INSERT INTO `unit_comments` VALUES (/*id*/1081008, /*unit_id*/108131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お父さん\n素敵なハロウィンなんだから\n女の子へのセクハラで\n台無しにしちゃダメだよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1081009, /*unit_id*/108131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"恥ずかしいですけど…\n一緒に踊ってもらえますか？\n霊のみなさんと\n踊り明かしたいんです");
INSERT INTO `unit_comments` VALUES (/*id*/1081010, /*unit_id*/108131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな幸せなハロウィンは\n初めてなので…\nつい、はしゃいでしまいます\n…あなたのおかげです");
INSERT INTO `unit_comments` VALUES (/*id*/1081011, /*unit_id*/108101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.3, /*change_face*/6, /*description*/"父もあなたのお誕生日を\n祝福しているみたいですね\nあ…お父さん！　\nこの人を噛んじゃダメ！");
INSERT INTO `unit_comments` VALUES (/*id*/1081012, /*unit_id*/108131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーハロウィン＆\nハッピーバースデー♪\n今日はお菓子ではなく、\nケーキをお渡ししますね");
INSERT INTO `unit_comments` VALUES (/*id*/1082001, /*unit_id*/108201, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.3, /*change_face*/1, /*description*/"いまのミヤコは、血も涙もない\n獰猛な狼女なの！\n食べられたくなければ、\nプリンをよこすの～！");
INSERT INTO `unit_comments` VALUES (/*id*/1082002, /*unit_id*/108201, /*use_type*/1, /*voice_id*/2, /*face_id*/3, /*change_time*/8.4, /*change_face*/2, /*description*/"カボチャプリン？\nそんなの邪道なの、認めないの！\n…ぱくっ…お、美味しいの～♪\nさっきの言葉は取り消すの～");
INSERT INTO `unit_comments` VALUES (/*id*/1082003, /*unit_id*/108201, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/7.2, /*change_face*/6, /*description*/"子どもたちの仮装にまじって、\nお菓子をもらいに来てる\n幽霊がたくさんいるの～\nほら、オマエの後ろにも！");
