INSERT INTO `unit_comments` VALUES (/*id*/1093005, /*unit_id*/109301, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.4, /*change_face*/1, /*description*/"オメメちゃんのことが\n気になるですか？\nつんつんってしてみます？");
INSERT INTO `unit_comments` VALUES (/*id*/1093006, /*unit_id*/109331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ルゥの華麗なオメメちゃんさばき\n見せてあげるですよ！\nそれそれですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1093007, /*unit_id*/109331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ルゥとオメメちゃんの\nこんびねーしょんで\nやっつけちゃうですよ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1093008, /*unit_id*/109331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"別の世界での思い出も、\nせいしゅんの１ページです！\nルゥ、とっても楽しいですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1093009, /*unit_id*/109331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え、オメメちゃんの正体ですか？\nさぁ…？　でも、ルゥとずーっと\n一緒のお友だちですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1093010, /*unit_id*/109331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"追試の女帝、落第の女王…\n数々のいみょうを持つルゥの実力\n見せてやるですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1093011, /*unit_id*/109301, /*use_type*/2, /*voice_id*/6, /*face_id*/6, /*change_time*/2.8, /*change_face*/2, /*description*/"今日はあなたの誕生日です？\nじゃあ、ルゥもお祝いするです！\nおめでとうですぅ！");
INSERT INTO `unit_comments` VALUES (/*id*/1093012, /*unit_id*/109331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ルゥ、暗記は苦手ですが…\nお友だちの誕生日は\nばっちり覚えてるですぅ！\nお誕生日おめでとうですよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1094001, /*unit_id*/109401, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.4, /*change_face*/4, /*description*/"私のこの姿や、竜の力…\nあなたは、\n怖がらないでいてくれる…？");
INSERT INTO `unit_comments` VALUES (/*id*/1094002, /*unit_id*/109401, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/3.6, /*change_face*/2, /*description*/"私はみんなみたいに\nはしゃげないけど…\nこれでも楽しんでるつもり");
INSERT INTO `unit_comments` VALUES (/*id*/1094003, /*unit_id*/109401, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.3, /*change_face*/2, /*description*/"アンは、ずっと一人だった私の\nそばにいてくれて…\n私の世界を照らしてくれたんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1094004, /*unit_id*/109401, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/"違う世界に来てしまうなんて…\nでも、あなたたちみたいな\n優しい人に出会えて、よかった");
INSERT INTO `unit_comments` VALUES (/*id*/1094005, /*unit_id*/109401, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/0.0, /*change_face*/2, /*description*/"あなたには本当に感謝してる…\nもし私に手伝えることがあれば\n何でもいってね、頑張るから");
INSERT INTO `unit_comments` VALUES (/*id*/1094006, /*unit_id*/109431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"皆を護るためなら、\nいくらでも力が出せるよ…！\n竜の炎を見せてあげる！");
INSERT INTO `unit_comments` VALUES (/*id*/1094007, /*unit_id*/109431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"皆のことは…\n絶対に傷つけさせない\n私の炎は、そのためにあるの…！");
INSERT INTO `unit_comments` VALUES (/*id*/1094008, /*unit_id*/109431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私に足りなかったのは、\n自分に負けない強さ…\nでも今は、もう大丈夫！");
INSERT INTO `unit_comments` VALUES (/*id*/1094009, /*unit_id*/109431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こんな力、いらないって\n思ったこともあるけれど…\n今は少し誇れる気がするんだ");
INSERT INTO `unit_comments` VALUES (/*id*/1094010, /*unit_id*/109431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、結構丈夫なんだ\nだから…任せて！");
INSERT INTO `unit_comments` VALUES (/*id*/1094011, /*unit_id*/109401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/4.3, /*change_face*/2, /*description*/"今日はあなたの誕生日なんだね\nその…おめでとう\nなにか欲しいものとかある？");
INSERT INTO `unit_comments` VALUES (/*id*/1094012, /*unit_id*/109431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーバースデー\nあなたのお祝いの日なのに、\n私までうれしいんだ。不思議だね");
INSERT INTO `unit_comments` VALUES (/*id*/1095001, /*unit_id*/109501, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/2.6, /*change_face*/2, /*description*/"これが噂の着物ですね…\n見てよし、\n脱がせてよし、\n回してよしの魔性の服…！");
INSERT INTO `unit_comments` VALUES (/*id*/1095002, /*unit_id*/109501, /*use_type*/1, /*voice_id*/2, /*face_id*/5, /*change_time*/2.9, /*change_face*/6, /*description*/"あ～れ～～～～～！\nドＳさん、見ていたのですか！？\nクウカの回る姿を楽しんでいたの\nですね…やはり人格破綻者…！");
INSERT INTO `unit_comments` VALUES (/*id*/1095003, /*unit_id*/109501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.8, /*change_face*/5, /*description*/"噂によると、着物では\n履かないものなのだとか…\nドッ、ドＳさん…確かめさせろ\nだなんていけませんよぉ…ぐふふ");
INSERT INTO `unit_comments` VALUES (/*id*/1095004, /*unit_id*/109501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/7.4, /*change_face*/5, /*description*/"あっ、帯が緩んでしまいました…\nドＳさん、締めなおしてくれるの\nですか？　…じゅるり。いい…\nいいですっ！　もっときつく～！");
INSERT INTO `unit_comments` VALUES (/*id*/1095005, /*unit_id*/109501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.6, /*change_face*/5, /*description*/"ドＳさん…いえ、今はドS様と\n呼んだ方がいいでしょうか…\nクウカはドＳ様にお仕えする\n従順な…じゅるり");
INSERT INTO `unit_comments` VALUES (/*id*/1095006, /*unit_id*/109531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この着物、露出が多くて\n少し恥ずかしいです…でも、\nドＳさんに見られていると思うと\nなんだか気持ちが…じゅるり");
INSERT INTO `unit_comments` VALUES (/*id*/1095007, /*unit_id*/109531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あ～～～れ～～～～～！\nこの回転がクセになりそうです\nもっと、もっと回ります～！\nぐふ、ぐふふふ～～～！");
INSERT INTO `unit_comments` VALUES (/*id*/1095008, /*unit_id*/109531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この衣装で踊ると、いつもより\nきれいに踊れている気がします…\n蝶のように舞うクウカを、\n蜘蛛のようにドＳさんが…ぐふふ");
INSERT INTO `unit_comments` VALUES (/*id*/1095009, /*unit_id*/109531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"「クウカの着物を剥ぎ取り、\nドＳさんは野獣のように…」\nぐふふ…はっ！　ドSさんに\n見られている気配がします…！");
INSERT INTO `unit_comments` VALUES (/*id*/1095010, /*unit_id*/109531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この帯が気になるんですか？\nつ、つまりクウカを回したいと…！\nそして着物のはだけたクウカを\nそのまま美味しく…ぐふふ");
INSERT INTO `unit_comments` VALUES (/*id*/1095011, /*unit_id*/109501, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/5.7, /*change_face*/5, /*description*/"今日は大切な日ですね…\nドＳさんが生まれた日…じゅるり\nつまりクウカの体でお祝いしろと\nいうのですね！？");
INSERT INTO `unit_comments` VALUES (/*id*/1095012, /*unit_id*/109531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nところでドＳさん、和服の帯って\nラッピングしてるみたいですよね\nプレゼントは私、なんて…ぐふふ");
INSERT INTO `unit_comments` VALUES (/*id*/1096001, /*unit_id*/109601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.9, /*change_face*/2, /*description*/"オシノビでお出かけするなら\nいいところを知ってマス\n今度ワタシが案内するデス！\nきっと楽しいデスよー！");
INSERT INTO `unit_comments` VALUES (/*id*/1096002, /*unit_id*/109601, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/4.9, /*change_face*/6, /*description*/"もみじも好きデスけど\nもみじマンジュウも好きデスよ\n考えてたらお腹が空きまシタ…\nハナヨリダンゴデス！");
INSERT INTO `unit_comments` VALUES (/*id*/1096003, /*unit_id*/109601, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/8.6, /*change_face*/2, /*description*/"ヒトツ投げては天下統一のため…\nフタツ投げても天下統一のため…\nショーグンも手裏剣投げの特訓を\nしまセンか？　投げ放題デス！");
INSERT INTO `unit_comments` VALUES (/*id*/1096004, /*unit_id*/109601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.0, /*change_face*/2, /*description*/"このキツネのお面はオーエド町の\n縁日で買いまシタ。歩くと鈴が\n鳴って、とてもかわいいデス\nコンコンデース！");
INSERT INTO `unit_comments` VALUES (/*id*/1096005, /*unit_id*/109601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/4.0, /*change_face*/1, /*description*/"忍法・氷結地獄を思いつきまシタ\n冷たいカキ氷を\n敵に無理やり食べさせて\n頭をキーーーンとさせる技デス！");
INSERT INTO `unit_comments` VALUES (/*id*/1096006, /*unit_id*/109631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンに仇名す敵に\n天誅デス！　天下統一するまで\n立ち止まりまセン！\n必殺忍法で勝ち進みマス！");
INSERT INTO `unit_comments` VALUES (/*id*/1096007, /*unit_id*/109631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"フッフッフ…とっておきの\n新忍法の見せどころデース！\nムム、まだ使っちゃダメデスか？\nショーグンのイケズデス～…");
INSERT INTO `unit_comments` VALUES (/*id*/1096008, /*unit_id*/109631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンは天下泰平の世を\n築いてくれるでショウ！\nワタシはそんな城下町を\nずっとお守りいたソウロウ！");
INSERT INTO `unit_comments` VALUES (/*id*/1096009, /*unit_id*/109631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ワタシのカチュヤク、しっかり\n見ていてクダサイ！　いつか\nチューシンと呼ばれるその日まで\nシロクジチュウおそばにいマス");
INSERT INTO `unit_comments` VALUES (/*id*/1096010, /*unit_id*/109631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ショーグンのためならどんな\n敵とだって戦うデス！\nチューセーシンこそ天下統一の\n切り札だと信じていマス！");
INSERT INTO `unit_comments` VALUES (/*id*/1096011, /*unit_id*/109601, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.4, /*change_face*/1, /*description*/"誕生日おめでとうデス！\nプレゼントはショーグン専用の\nマイ手裏剣デース！\nいっぱい投げ込んでクダサーイ！");
INSERT INTO `unit_comments` VALUES (/*id*/1096012, /*unit_id*/109631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日はおめでとうゴザルデス！\n乾杯の音頭はワタシがとりマス！\nお手をハイシャクデース！\n朝までワッショイデース！");
INSERT INTO `unit_comments` VALUES (/*id*/1097001, /*unit_id*/109701, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/"家事全般は得意ですので、\nなにかお役に\n立てることがありましたら、\nこのレムにお申し付けください");
INSERT INTO `unit_comments` VALUES (/*id*/1097002, /*unit_id*/109701, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.8, /*change_face*/2, /*description*/"姉様がメイドの仕事を\nしないで休憩している…ですか？\nさすが姉様！\nお休みする姿もすてきです！");
INSERT INTO `unit_comments` VALUES (/*id*/1097003, /*unit_id*/109701, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.0, /*change_face*/2, /*description*/"鬼といっても、\n人間と大きく変わりませんよ\n魔獣を薙ぎ払ったり、\n鉄球を振り回せるぐらいです");
INSERT INTO `unit_comments` VALUES (/*id*/1097004, /*unit_id*/109701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"身の回りの世話は\nこのレムにお任せください\n懸命にご奉仕\nさせていただきます");
INSERT INTO `unit_comments` VALUES (/*id*/1097005, /*unit_id*/109701, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.1, /*change_face*/2, /*description*/"こちらの世界では、\n角があっても\n珍しくはないのですね\nなんだか、少しうれしいです");
INSERT INTO `unit_comments` VALUES (/*id*/1097006, /*unit_id*/109731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この鉄球ですか？\nこれは携帯用で、\n短くなっているから、\n普通の鉄球より軽いんですよ");
INSERT INTO `unit_comments` VALUES (/*id*/1097007, /*unit_id*/109731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"巨木であろうと、\n鬼であるレムを\n阻むことは不可能です\n全て粉砕させていただきます");
INSERT INTO `unit_comments` VALUES (/*id*/1097008, /*unit_id*/109731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"この程度の巨木ならば、\nレムの力でどけられると思います\nあ、あの…上手くいったら\n褒めてくれてもいいんですよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1097009, /*unit_id*/109731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"女性にこの倒木を\n破壊するのは無理、ですか？\nご託は結構ですので、\nそこで見ていてください");
INSERT INTO `unit_comments` VALUES (/*id*/1097010, /*unit_id*/109731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"強いって…\n女の子にその言葉は\nどうかと思いますよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1097011, /*unit_id*/109701, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/5.4, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nお祝いにケーキを焼きますね\nより豪華にするため、まずは\n飛竜の卵を調達してこなくては");
INSERT INTO `unit_comments` VALUES (/*id*/1097012, /*unit_id*/109731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\n今日のために色々と\n用意させていただきましたので、\n精いっぱいお祝いいたしますね");
INSERT INTO `unit_comments` VALUES (/*id*/1098001, /*unit_id*/109801, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/5.0, /*change_face*/3, /*description*/"ここが異世界。正直、\n信じられるものでもないけど…\nバルスの軽口よりは\nいくらかましかもしれないわね");
INSERT INTO `unit_comments` VALUES (/*id*/1098002, /*unit_id*/109801, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/2.4, /*change_face*/3, /*description*/"面倒なことになったものね\nまぁ、危険人物はいないようだし\n楽できる分には問題ないわ");
INSERT INTO `unit_comments` VALUES (/*id*/1098003, /*unit_id*/109801, /*use_type*/1, /*voice_id*/3, /*face_id*/3, /*change_time*/1.9, /*change_face*/2, /*description*/"仕事ならしているわよ\n紅茶を優雅に飲むラムの姿が、\n周りの目を楽しませているわ");
INSERT INTO `unit_comments` VALUES (/*id*/1098004, /*unit_id*/109801, /*use_type*/1, /*voice_id*/4, /*face_id*/3, /*change_time*/5.4, /*change_face*/4, /*description*/"これは風の系統魔法よ\nその気になれば、\n何でも細切れにできるわ\n疲れるから、やらないけど");
INSERT INTO `unit_comments` VALUES (/*id*/1098005, /*unit_id*/109801, /*use_type*/1, /*voice_id*/5, /*face_id*/3, /*change_time*/5.9, /*change_face*/2, /*description*/"蒸かし芋を食らうがいいわ\nおいしかったでしょう\n出来たて…いえ、\nふかしたてよ");
INSERT INTO `unit_comments` VALUES (/*id*/1098006, /*unit_id*/109831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"千里眼をもつラムからは\n逃げられないわよ\n死にたくなければ、せいぜい\n知恵と勇気を振り絞ることね");
INSERT INTO `unit_comments` VALUES (/*id*/1098007, /*unit_id*/109831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"覚えておきなさい\n角をなくそうと、\n天才であるラムには\n遠くおよばないということを");
INSERT INTO `unit_comments` VALUES (/*id*/1098008, /*unit_id*/109831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"戦闘では本当に役立たずね\n失望を隠すのが大変だわ\n…せいぜいラムの側から\n離れないことね");
INSERT INTO `unit_comments` VALUES (/*id*/1098009, /*unit_id*/109831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"一気に決めさせてもらうわ\nなぜって？\nラムが…いえ、\n早く帰りたいからよ");
INSERT INTO `unit_comments` VALUES (/*id*/1098010, /*unit_id*/109831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"風の魔法は便利よ。敵を\n切り裂くだけでなく、面倒な男を\nねじ切ることだってできるわ\nどこを？　ご想像にお任せするわ");
INSERT INTO `unit_comments` VALUES (/*id*/1098011, /*unit_id*/109801, /*use_type*/2, /*voice_id*/6, /*face_id*/3, /*change_time*/6.5, /*change_face*/2, /*description*/"誕生日をラムに祝ってもらえる\nなんて光栄に思うことね\n誕生日プレゼント？　もちろん\n出来たての蒸かし芋よ！");
INSERT INTO `unit_comments` VALUES (/*id*/1098012, /*unit_id*/109831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何が欲しいの？\n言ってごらんなさい\nなんでって、誕生日なんでしょ？\nご褒美くらいくれてやるわ");
INSERT INTO `unit_comments` VALUES (/*id*/1099001, /*unit_id*/109901, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/7.9, /*change_face*/1, /*description*/"不思議なものね\nマナもあって精霊もいるのに\n見たこともないものがいっぱい\nここが異世界なんだ…");
INSERT INTO `unit_comments` VALUES (/*id*/1099002, /*unit_id*/109901, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"軽くお散歩でもする？\n精霊の子たちとお話ししにいくの\nそれがこの子たちとの\n契約条件の一つだから");
INSERT INTO `unit_comments` VALUES (/*id*/1099003, /*unit_id*/109901, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.0, /*change_face*/6, /*description*/"さっき街に行ったら\nエルフの人をみかけたわ\nもしかしてこの世界にも\nハーフエルフっているのかな…？");
INSERT INTO `unit_comments` VALUES (/*id*/1099004, /*unit_id*/109901, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/5.2, /*change_face*/6, /*description*/"コッコロちゃんって賢くて\nすごーくかわいいわよね\n…本当に私の妹だったら\nよかったのにな");
INSERT INTO `unit_comments` VALUES (/*id*/1099005, /*unit_id*/109901, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/5.4, /*change_face*/6, /*description*/"こっちの世界に来てから\nパックってあまり出てこないの\nマナを使いすぎないように\n気を使ってるのかな");
INSERT INTO `unit_comments` VALUES (/*id*/1099006, /*unit_id*/109931, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あなたも少し座らない？\nランドソルの花もすごーくきれい\n微精霊たちもよろこんでるわ");
INSERT INTO `unit_comments` VALUES (/*id*/1099007, /*unit_id*/109931, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"少しお話しましょう？\nこの世界のことや\n私の世界のこと…\n話したいことがたくさんあるの");
INSERT INTO `unit_comments` VALUES (/*id*/1099008, /*unit_id*/109931, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"そっか、コッコロちゃんも\n精霊たちとお話しできるのね\nふふ、やっぱり他人とは\n思えないな");
INSERT INTO `unit_comments` VALUES (/*id*/1099009, /*unit_id*/109931, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あら、花びらが髪についてる\nとってあげるから\n頭をこっちに向けて？\nそうそう…はい、とれたよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1099010, /*unit_id*/109931, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ねえ…またこのお花畑を\n一緒に見に来てくれる？\n…精霊使いにとって約束は\n何よりも大切なんだからね");
INSERT INTO `unit_comments` VALUES (/*id*/1099011, /*unit_id*/109901, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.0, /*change_face*/2, /*description*/"こっちの世界だと\nはっぴーばーすでー\nでいいのかな？\nお誕生日おめでとう");
INSERT INTO `unit_comments` VALUES (/*id*/1099012, /*unit_id*/109931, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日なんだね、おめでとう\nこうやってお祝いできて\nすごーくうれしいな");
INSERT INTO `unit_comments` VALUES (/*id*/1100001, /*unit_id*/110001, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.9, /*change_face*/2, /*description*/"ちょす～♪\n夏の海ってキラキラしてて\nマジヤバいよね！\nめいっぱい楽しんじゃお～！");
INSERT INTO `unit_comments` VALUES (/*id*/1100002, /*unit_id*/110001, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.9, /*change_face*/6, /*description*/"ヒデサイ、知ってた？\n海の水ってしょっぱいんだよ！\nうち物知りっしょ～♪\nでぃすいずあんだすたんど？");
INSERT INTO `unit_comments` VALUES (/*id*/1100003, /*unit_id*/110001, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.4, /*change_face*/1, /*description*/"撮影が終わったら\n一緒に海で遊ぼうよ！\nすぐ終わるから\nちょいまっててね♪");
INSERT INTO `unit_comments` VALUES (/*id*/1100004, /*unit_id*/110001, /*use_type*/1, /*voice_id*/4, /*face_id*/2, /*change_time*/2.5, /*change_face*/5, /*description*/"この水着、イケてるっしょ！\nファンのみんなに\n喜んでもらえるといいな～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1100005, /*unit_id*/110001, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/7.7, /*change_face*/1, /*description*/"んー！　海って最高ー！\nヒデサイといっぱい遊べるし\nマジで来てよかったよー♪\n今は夏の宿題の話はナシでよろ～");
INSERT INTO `unit_comments` VALUES (/*id*/1100006, /*unit_id*/110031, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"見て見て、うちのモデル姿！\nどう？　いい感じっしょ♪\nこんなに近くで見ていいのは\nヒデサイだけだかんね？");
INSERT INTO `unit_comments` VALUES (/*id*/1100007, /*unit_id*/110031, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の海って楽しいことが\nマジでいっぱいあるけど…\nヒデサイといるだけで、めっちゃ\nバイブス上がるんだよね～");
INSERT INTO `unit_comments` VALUES (/*id*/1100008, /*unit_id*/110031, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"セクシーってよく分かんないけど\nいまのうち、セクシーかな？\nヒデサイがドキドキ\nしてたら、うれしいな♪");
INSERT INTO `unit_comments` VALUES (/*id*/1100009, /*unit_id*/110031, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏って気分アガるよね♪\nヒデサイも応援してくれるし、\nもう怖いもんなしって感じだよ☆");
INSERT INTO `unit_comments` VALUES (/*id*/1100010, /*unit_id*/110031, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"日ざしも砂浜もあっついねー！\n今日はめっちゃ\nフィーリングがいいんだ～♪\n超かわいい写真が撮れそう！");
INSERT INTO `unit_comments` VALUES (/*id*/1100011, /*unit_id*/110001, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/3.3, /*change_face*/2, /*description*/"ちょすー！\nヒデサイ、誕生日なんだよね？\nおめでとー！\n今日はパーティーだね！");
INSERT INTO `unit_comments` VALUES (/*id*/1100012, /*unit_id*/110031, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ちょす！　ヒデサイ、ハピバー☆\nお祝いしなきゃだよね！\nうちにしてほしいことがあったら\nなんでも言っていいよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1101001, /*unit_id*/110101, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/7.4, /*change_face*/2, /*description*/"海に入る前には\n準備運動をしなくちゃ！\n手伝ってあげるから力を抜いて？\nそう、とっても上手よ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1101002, /*unit_id*/110101, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/7.1, /*change_face*/1, /*description*/"海って楽しいところだけど\n危ないこともあるんだから\n気を付けて遊びましょうね？\n先生がついていてあげる");
INSERT INTO `unit_comments` VALUES (/*id*/1101003, /*unit_id*/110101, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/4.8, /*change_face*/4, /*description*/"なんだかやたら男の人が\n話しかけてくるの。もしかして\n水着がずれちゃってるのかな\n君、ちょっと見てくれる？");
INSERT INTO `unit_comments` VALUES (/*id*/1101004, /*unit_id*/110101, /*use_type*/1, /*voice_id*/4, /*face_id*/5, /*change_time*/7.0, /*change_face*/4, /*description*/"ああ～ん、\n砂が水着の中に入ってきちゃって\nくすぐったいわ～\nお願い、なんとかして～");
INSERT INTO `unit_comments` VALUES (/*id*/1101005, /*unit_id*/110101, /*use_type*/1, /*voice_id*/5, /*face_id*/5, /*change_time*/8.0, /*change_face*/6, /*description*/"久しぶりに水着を着るから\n今日のために新調したのよ\n似合ってるかな？　ってどうして\n顔をそむけちゃうのよ～");
INSERT INTO `unit_comments` VALUES (/*id*/1101006, /*unit_id*/110131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"くしゅんっ…\nちょっと冷えてきちゃった\n君にくっついて\n暖まってもいいかな？");
INSERT INTO `unit_comments` VALUES (/*id*/1101007, /*unit_id*/110131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君があまりにも頼もしいから\n私たちが先生と生徒って事を\n忘れちゃいそうなの…\nそれでも…ううん、なんでもない");
INSERT INTO `unit_comments` VALUES (/*id*/1101008, /*unit_id*/110131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"こうして男の子と海にくるなんて\n初めてのことだから\nなんだか照れちゃうわ\nねえ、君はいまどんな気持ち？");
INSERT INTO `unit_comments` VALUES (/*id*/1101009, /*unit_id*/110131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏休みの一番の思い出は\nこのきれいな夜の海かな\n君と一緒に見たんだから\n一生忘れないわ、きっと…");
INSERT INTO `unit_comments` VALUES (/*id*/1101010, /*unit_id*/110131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"君、もしかして少し背が伸びた？\n気のせいじゃないと思うよ\n君の成長を感じると、なんだか\nうれしくて胸がドキドキするから");
INSERT INTO `unit_comments` VALUES (/*id*/1101011, /*unit_id*/110101, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.2, /*change_face*/2, /*description*/"今日は君のお誕生日ね\nふふ、もちろん忘れてないわ\n一つ大人になったお祝いに\n海の家でパーティーしましょ！");
INSERT INTO `unit_comments` VALUES (/*id*/1101012, /*unit_id*/110131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとう～\n浜辺で見る君の顔が\nすっかり大人にみえるわ\nふふ、夏のせいかしらね");
INSERT INTO `unit_comments` VALUES (/*id*/1103001, /*unit_id*/110301, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.9, /*change_face*/1, /*description*/"あははっ\n日が暮れるまで浜辺でゆっくり…\nこんな日があってもいいわよね");
INSERT INTO `unit_comments` VALUES (/*id*/1103002, /*unit_id*/110301, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/6.0, /*change_face*/6, /*description*/"貝殻を集めてアクセサリー作り\nなんてのも悪くないわね\n思い出作りよ？　売ろうなんて\n考えてないわよ？　たぶんね");
INSERT INTO `unit_comments` VALUES (/*id*/1103003, /*unit_id*/110301, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"海の砂ってサラサラしてて気持ち\nいいわよね。持ち帰って救護院に\n砂場を作ったりできないかしら？");
INSERT INTO `unit_comments` VALUES (/*id*/1103004, /*unit_id*/110301, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/4.9, /*change_face*/5, /*description*/"子どもたちと賑やかに\n過ごすのはもちろん楽しいけど\nあんたと二人きりで過ごすのも\nなかなかいいわね");
INSERT INTO `unit_comments` VALUES (/*id*/1103005, /*unit_id*/110301, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/3.1, /*change_face*/2, /*description*/"あたしの水着をこんな間近で\n見られるなんて、あんた本当に\n得してるわよ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1103006, /*unit_id*/110331, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"エレガントじゃないかもだけど\n今だけは許してね\nあたし今、とびっきり\n舞い上がっちゃってる！");
INSERT INTO `unit_comments` VALUES (/*id*/1103007, /*unit_id*/110331, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の日差しって、開放的な\n気分にさせるわよね！\nあたしももう少し\n冒険してみようかしら♪");
INSERT INTO `unit_comments` VALUES (/*id*/1103008, /*unit_id*/110331, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"海に来るのなんて久しぶりね～\nせっかくのバカンスなんだから\n楽しまなきゃ損よ！");
INSERT INTO `unit_comments` VALUES (/*id*/1103009, /*unit_id*/110331, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"本当にきれいな海よね\nもし誰もいない浜辺であんたと\n二人っきりだったら…って！\nなんであたしそんなことを！");
INSERT INTO `unit_comments` VALUES (/*id*/1103010, /*unit_id*/110331, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"捕まえてみなさ～い♪\nな、なんてことあたしが\nできるわけないじゃない！！");
INSERT INTO `unit_comments` VALUES (/*id*/1103011, /*unit_id*/110301, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/1.9, /*change_face*/1, /*description*/"誕生日おめでとう♪\n救護院のみんなもあんたの\n誕生日を祝いたがってるから\n楽しみにしてなさいね！");
INSERT INTO `unit_comments` VALUES (/*id*/1103012, /*unit_id*/110331, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日おめでとう！\n二人きりでバースデーパーティー\nしましょうか♪　なんてね");
INSERT INTO `unit_comments` VALUES (/*id*/1104001, /*unit_id*/110401, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/2.9, /*change_face*/6, /*description*/"へへっ、海だー！\nな、なんだよ、あたしが\nはしゃいじゃおかしいかよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1104002, /*unit_id*/110401, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/2.6, /*change_face*/2, /*description*/"ビーチで焼きそばもいいよな\nよし！　材料持っていって\n昼飯は作ってやるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1104003, /*unit_id*/110401, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.2, /*change_face*/1, /*description*/"暑い日に泳ぐのって最高だよな！\nそうだ、浮き輪引っ張って\n泳いでやろうか？");
INSERT INTO `unit_comments` VALUES (/*id*/1104004, /*unit_id*/110401, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/3.3, /*change_face*/5, /*description*/"おまえと一緒だと、なんか楽…\nなっ、なんでもねぇ！\n…海だからって、開放的に\nなりすぎるのもよくないな、うん");
INSERT INTO `unit_comments` VALUES (/*id*/1104005, /*unit_id*/110401, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/8.0, /*change_face*/6, /*description*/"ちょっと休憩しようぜ\nお、なんかおまえの手、\nひんやりして冷たいな！\nって…わりぃ、触りすぎたな");
INSERT INTO `unit_comments` VALUES (/*id*/1104006, /*unit_id*/110431, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うぉわっ！？　み、水着がっ！？\nだ、誰も見てねぇ、よな…って\nなんでそこにいやがんだよ！？\n見てんじゃねぇ！　…ったく");
INSERT INTO `unit_comments` VALUES (/*id*/1104007, /*unit_id*/110431, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ん～、風が気持ちいいな！\nへへっ、おまえと二人で海なんて、\nくすぐったい感じだぜ\nさぁて、まずは何をする？");
INSERT INTO `unit_comments` VALUES (/*id*/1104008, /*unit_id*/110431, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"なんか、おまえには恥ずかしいとこ\nばかり見られてる気がするな…\nイヤってわけじゃねぇけどよ\n…って何言ってんだあたしは！？");
INSERT INTO `unit_comments` VALUES (/*id*/1104009, /*unit_id*/110431, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"不思議なんだけどさ\nおまえといると、時間が\nあっという間に過ぎちまう…\nおまえもか？　…そ、そっか！");
INSERT INTO `unit_comments` VALUES (/*id*/1104010, /*unit_id*/110431, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"か、かわいい？\nあたしの水着姿が？\nう、うれしくねぇっての！\n尻尾？　振ってねぇ！　見んな！");
INSERT INTO `unit_comments` VALUES (/*id*/1104011, /*unit_id*/110401, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/8.7, /*change_face*/2, /*description*/"誕生日なんだってな。そうだな…\n砂浜特訓メニューのプレゼント、\nってのは冗談で、\nあとでカキ氷でもおごってやるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1104012, /*unit_id*/110431, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日、誕生日なんだろ？\n食材の買い出し、付き合えよ\nあとでなんか作ってやるからさ\nおまえの好きなもの…教えろよな");
INSERT INTO `unit_comments` VALUES (/*id*/1105001, /*unit_id*/110501, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/1.9, /*change_face*/1, /*description*/"元気してるさー？\n私はいつでも元気だよー！\nさーさ、一緒に何するさー？");
INSERT INTO `unit_comments` VALUES (/*id*/1105002, /*unit_id*/110501, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/3.2, /*change_face*/1, /*description*/"なんくるないさー！\n海を見ると叫びたくならないー？\nほらキミも、なんくるないさー！");
INSERT INTO `unit_comments` VALUES (/*id*/1105003, /*unit_id*/110501, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/5.7, /*change_face*/2, /*description*/"海は楽しいことでいっぱいよー！\n走って、泳いで、歌って、踊って\n思う存分楽しむさー！");
INSERT INTO `unit_comments` VALUES (/*id*/1105004, /*unit_id*/110501, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/8.2, /*change_face*/1, /*description*/"ん～？　どこを見てるのー？\nこの水着が気になるさー？\nじゃあ触ってみるー？　\nほらほら、遠慮はいらないさ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1105005, /*unit_id*/110501, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/3.1, /*change_face*/5, /*description*/"波の音は心地いいさ～\nキミと一緒に聞いてるとなんだか\n胸がドキドキするね～\nキミも？　聞いてみていい～？");
INSERT INTO `unit_comments` VALUES (/*id*/1105006, /*unit_id*/110531, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の楽しみ方は色々あるけど～\nこうして演奏するのは格別さ～\n聞こえる波のリズムに乗って\n心のままに音楽を楽しむさー！");
INSERT INTO `unit_comments` VALUES (/*id*/1105007, /*unit_id*/110531, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あは～♪　楽しいねぇ～！\n夏は体がウズウズするさ～！\nほら、キミも一緒に歌うさ～\n恥ずかしがってちゃダメだよ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1105008, /*unit_id*/110531, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"思う存分歌ったあとは\n浜辺を走って、海で泳ぐよー！\n夏は待っててくれないからね～\n今日は私と限界を超えるさ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1105009, /*unit_id*/110531, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今度、キミをテーマに\n一曲作ってみたいさ～\nきっと楽しい曲になるさ～！");
INSERT INTO `unit_comments` VALUES (/*id*/1105010, /*unit_id*/110531, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"キミのことを考えて演奏すると\n音がいつもと違う感じになるさー\nなんて言えばいいのかな～…\nとにかく聴いてほしいさー！");
INSERT INTO `unit_comments` VALUES (/*id*/1105011, /*unit_id*/110501, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/2.8, /*change_face*/1, /*description*/"ばっちり準備万端さー！\n何って、キミの誕生日だよー！\nさぁ手と手を繋いで、\nレッツダンスさー！");
INSERT INTO `unit_comments` VALUES (/*id*/1105012, /*unit_id*/110531, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"誕生日、めでたいさ～！\n体が勝手に踊りだすさ～、あっ！\nぶつかってごめんねぇ、でも\nやわいとこだから平気だよねー？");
INSERT INTO `unit_comments` VALUES (/*id*/1106001, /*unit_id*/110601, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/4.2, /*change_face*/2, /*description*/"夏の海は不思議の国どすえ\nうちと一緒に\n楽しく過ごしまひょ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106002, /*unit_id*/110601, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.5, /*change_face*/1, /*description*/"ふう、日差しが暑くて\nかなわんわぁ～\n王子はん、あっちの日陰で\n涼みまへんか♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106003, /*unit_id*/110601, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.1, /*change_face*/2, /*description*/"冷たいスイカ、切ってきましたぇ\nぬるならへんうちに一緒に\n食べまひょ～");
INSERT INTO `unit_comments` VALUES (/*id*/1106004, /*unit_id*/110601, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/7.9, /*change_face*/5, /*description*/"夏と言えば、花火どす\n夜空に咲く大輪の花…\nはぁ、王子はんと二人で\n見に行きたいわぁ");
INSERT INTO `unit_comments` VALUES (/*id*/1106005, /*unit_id*/110601, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/5.0, /*change_face*/2, /*description*/"この水着、\nうちに似合うてますやろか？\n王子はんに見せたくて\n一生懸命選んだんどす♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106006, /*unit_id*/110631, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いやぁ、気持ちええわぁ～♪\nこうして海辺で寝転がってると\n夏を全身で感じられそうやわぁ\n王子はんも一緒に、どうやろか？");
INSERT INTO `unit_comments` VALUES (/*id*/1106007, /*unit_id*/110631, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ぬいぐるみのみんなも\nお日さま浴びて気持ちよさそうや\n王子はんもこっち来て触ります？\nふかふか、もふもふですよ…♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106008, /*unit_id*/110631, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"うちの水着、\nヒラヒラしててかいらしやろ？\nじっくり見てもええんよ？\nもっと近くで、ほら、な♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106009, /*unit_id*/110631, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夏の海辺はロマンチックやねぇ\nこんな場所に王子はんと二人きり\nゆったり暮らしたいどすなぁ\n…お城はどこに建てまひょか♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106010, /*unit_id*/110631, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"王子はん、一緒に踊りまへんか？\nマホ姫と王子はん、二人だけの\n海辺の舞踏会や♪\nさぁ、一緒に…くるりんぱ～♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106011, /*unit_id*/110601, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/6.2, /*change_face*/2, /*description*/"お誕生日おめでとうなぁ～\nまだまだ暑いさかい\n今日は、和菓子と冷やし抹茶で\nお祝いしまひょ♪");
INSERT INTO `unit_comments` VALUES (/*id*/1106012, /*unit_id*/110631, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"今日は王子はんの誕生日やねぇ\nほんま、めでたい日やわぁ\n魔法でお祝いの花火\nあげるからしっかり見といてな");
INSERT INTO `unit_comments` VALUES (/*id*/1107001, /*unit_id*/110701, /*use_type*/1, /*voice_id*/1, /*face_id*/6, /*change_time*/3.0, /*change_face*/4, /*description*/"制服って、スカートなんですね…\nうう…足元がすーすーして\n落ち着かないです…");
INSERT INTO `unit_comments` VALUES (/*id*/1107002, /*unit_id*/110701, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/3.7, /*change_face*/2, /*description*/"私が学院に行くのが心配ですか？\nご安心ください！\nいざとなったら、\n保健室登校しますから！");
INSERT INTO `unit_comments` VALUES (/*id*/1107003, /*unit_id*/110701, /*use_type*/1, /*voice_id*/3, /*face_id*/6, /*change_time*/2.7, /*change_face*/2, /*description*/"学院にも植物が…！\nぼっちのままでも、\nお花さんたちに\n話しかければ寂しくないです！");
INSERT INTO `unit_comments` VALUES (/*id*/1107004, /*unit_id*/110701, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/6.3, /*change_face*/2, /*description*/"ＢＢ団の特訓が無かったら、\n学院に近づくだけで\n失神していたかも…えへへ♪\nあなたには、とっても感謝です♪");
INSERT INTO `unit_comments` VALUES (/*id*/1107005, /*unit_id*/110701, /*use_type*/1, /*voice_id*/5, /*face_id*/4, /*change_time*/7.2, /*change_face*/5, /*description*/"あ、あの…\n私の制服姿はどうでしょうか？\nえ…か、かわいいって…\nあわ、あわわわわ…！");
INSERT INTO `unit_comments` VALUES (/*id*/1107006, /*unit_id*/110731, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"とにかく目立たない！\nそして、常に隅っこに！\nこうすれば、ぼっちの私でも\n平和な学院生活が…ひぃ！！");
INSERT INTO `unit_comments` VALUES (/*id*/1107007, /*unit_id*/110731, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あ、あの人と一緒の学院…\nなんだかドキドキして、\n授業に集中できな…はうぅ！？\nちゃんと授業も頑張ります～！");
INSERT INTO `unit_comments` VALUES (/*id*/1107008, /*unit_id*/110731, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"つ、次の授業、団体行動がある…\n保健室に逃げ込むしか…ひぃぃ！\nう、嘘です嘘です！\nそんなこと考えてませんから～！");
INSERT INTO `unit_comments` VALUES (/*id*/1107009, /*unit_id*/110731, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きょ、今日は友達運が最高！？\n誰かに話しかけられるかもって…\n早速話しかけられる練習を\n始めないと…！");
INSERT INTO `unit_comments` VALUES (/*id*/1107010, /*unit_id*/110731, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"きょ、今日こそ、\nあの人と下校を…はわぁ！\nご、ごめんなさい！\n私には大それた願いでした！");
INSERT INTO `unit_comments` VALUES (/*id*/1107011, /*unit_id*/110701, /*use_type*/2, /*voice_id*/6, /*face_id*/5, /*change_time*/6.8, /*change_face*/2, /*description*/"お、おお、お誕生日\nおめでとうございます！\nああ…他の誰かの誕生日を\n祝えるだけでも幸せです…");
INSERT INTO `unit_comments` VALUES (/*id*/1107012, /*unit_id*/110731, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます！\nこんな日に\nあなたと一緒に居られるなんて…\n友達ができるよりうれしいです♪");
INSERT INTO `unit_comments` VALUES (/*id*/1108001, /*unit_id*/110801, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/6.1, /*change_face*/2, /*description*/"いいけど、\nなんかガッコーとかだと\nうち謎に恐がられてンだよね\nあんたは違うみたいだけどさ");
INSERT INTO `unit_comments` VALUES (/*id*/1108002, /*unit_id*/110801, /*use_type*/1, /*voice_id*/2, /*face_id*/6, /*change_time*/5.1, /*change_face*/1, /*description*/"ちょっと小耳に挟んだんだけど、\nあんたお金食べようとしたこと\nあんだって？　ウケる\nアレあんま美味しくないよね");
INSERT INTO `unit_comments` VALUES (/*id*/1108003, /*unit_id*/110801, /*use_type*/1, /*voice_id*/3, /*face_id*/2, /*change_time*/3.0, /*change_face*/4, /*description*/"いらっしゃいませぇ～！\n…や、なんかバイト先で愛想よく\nしろとか言われて。はーマジだる");
INSERT INTO `unit_comments` VALUES (/*id*/1108004, /*unit_id*/110801, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.4, /*change_face*/2, /*description*/"なんてーか、あんたのバイブス\nわりと悪くないんじゃない\n知んないけど");
INSERT INTO `unit_comments` VALUES (/*id*/1108005, /*unit_id*/110801, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/3.1, /*change_face*/5, /*description*/"うわ、きも…\n何カッコつけてんの\n不覚にも一瞬キュンって\n来ちゃったじゃん…うち、きもっ");
INSERT INTO `unit_comments` VALUES (/*id*/1108006, /*unit_id*/110831, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜っていいよね。なんてゆか…\n色々見えすぎないカンジとか\n言えないこと言えちゃうノリとか\nあと…なんかカッコいいじゃん");
INSERT INTO `unit_comments` VALUES (/*id*/1108007, /*unit_id*/110831, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"何。あんたまた道に迷ってんの\nしゃーないなー、送ってくよ\nいや、礼とかいいって、べつに\nなんかそんな気分なんだよね");
INSERT INTO `unit_comments` VALUES (/*id*/1108008, /*unit_id*/110831, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"夜に佇むミステリアスな少女…\nどーよコレ。カンジ出てない？\nただの夜勤休憩中なんだけどさ\nいいの、こういうのは気分だから");
INSERT INTO `unit_comments` VALUES (/*id*/1108009, /*unit_id*/110831, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あのさ。あんたのことだから\nべつに意味とか無いンだろうけど\n無言で人のカオ見んのやめて\nなんか謎にモジついて来るから");
INSERT INTO `unit_comments` VALUES (/*id*/1108010, /*unit_id*/110831, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あんたが？　送ってくれんの？\n何ソレ、誰が誰に言ってんのよ\n夜道は危ないって？　ウケる\nちょっとカッコいいじゃん");
INSERT INTO `unit_comments` VALUES (/*id*/1108011, /*unit_id*/110801, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/9.3, /*change_face*/5, /*description*/"あのさ。弟たちに作ったケーキ、\n余ってるから食べてくんない\nべつに無理にとは言わないけど\nあー…あと、おたおめ");
INSERT INTO `unit_comments` VALUES (/*id*/1108012, /*unit_id*/110831, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"いいけど、あんた、\n今日が誕生日なんだって？\nおめでと。よかったね\nキラーペンシルあげるよ");
INSERT INTO `unit_comments` VALUES (/*id*/1111001, /*unit_id*/111101, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/4.9, /*change_face*/6, /*description*/"ハロウィンですね…\nみんな仮装して、楽しそうです\n…怖いわけじゃありませんよ？");
INSERT INTO `unit_comments` VALUES (/*id*/1111002, /*unit_id*/111101, /*use_type*/1, /*voice_id*/2, /*face_id*/4, /*change_time*/6.0, /*change_face*/6, /*description*/"カボチャがいっぱいです…\nみんな顔があって、うぅ…\nな、なんで被ってるんですか！\nビックリさせないでください！");
INSERT INTO `unit_comments` VALUES (/*id*/1111003, /*unit_id*/111101, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/8.7, /*change_face*/5, /*description*/"ど、どうでしょうか、この衣装…\n私は子供っぽいと思うんですけど\n…似合ってる？　そ、そうですか\nだったら…よかったです");
INSERT INTO `unit_comments` VALUES (/*id*/1111004, /*unit_id*/111101, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/11.5, /*change_face*/6, /*description*/"あなたは仮装しないんですか？\nどんな衣装が似合うか、ですか？\nえっと…う～ん…吸血鬼さん？\nあ、血は吸わないでくださいね！");
INSERT INTO `unit_comments` VALUES (/*id*/1111005, /*unit_id*/111101, /*use_type*/1, /*voice_id*/5, /*face_id*/6, /*change_time*/6.0, /*change_face*/5, /*description*/"さっきからどうして\n頭を撫でてるんですか！\nかわいいから、って…\nもう！　もう～～～っ！");
INSERT INTO `unit_comments` VALUES (/*id*/1111006, /*unit_id*/111131, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お菓子、ありますか？\nない？　だったらイタズラですね\nか、噛みついちゃいますから！\nえ…えーいっ！　かぷっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1111007, /*unit_id*/111131, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私は子供じゃないので言いません\n絶対に言いませんから！　え？\nお菓子、おいしいんですか？\n…トリック・オア・トリート…");
INSERT INTO `unit_comments` VALUES (/*id*/1111008, /*unit_id*/111131, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"私、カップケーキ作ったんです\n食べますか？　え？\nイタズラの方がいい？\nへんた～い！　もうあげません！");
INSERT INTO `unit_comments` VALUES (/*id*/1111009, /*unit_id*/111131, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハロウィン、\n一緒に…回りませんか？\n怖いんじゃありません！　もう、\nあなたはしょうがない人ですね！");
INSERT INTO `unit_comments` VALUES (/*id*/1111010, /*unit_id*/111131, /*use_type*/1, /*voice_id*/5, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"え？　お菓子、くれるんですか？\n……甘くて、おいしいです\nあ、手についちゃった…ぺろっ\nなっ、なに見てるんですかぁっ！");
INSERT INTO `unit_comments` VALUES (/*id*/1111011, /*unit_id*/111101, /*use_type*/2, /*voice_id*/6, /*face_id*/2, /*change_time*/7.1, /*change_face*/6, /*description*/"あなたのお誕生日…これ、どうぞ\n私が作ったカップケーキです\nあっ、そんなに急いで食べたら\nポロポロこぼしちゃいますよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1111012, /*unit_id*/111131, /*use_type*/2, /*voice_id*/6, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"お誕生日おめでとうございます\nこれ、パンプキンケーキです\nカボチャは少し苦手ですけど、\n頑張って味見もしたので…！");
INSERT INTO `unit_comments` VALUES (/*id*/1112001, /*unit_id*/111201, /*use_type*/1, /*voice_id*/1, /*face_id*/2, /*change_time*/5.5, /*change_face*/6, /*description*/"ハロウィンって\nいくらいたずらしても\n怒られないから、最高だよね！\nえ？　違うの？");
INSERT INTO `unit_comments` VALUES (/*id*/1112002, /*unit_id*/111201, /*use_type*/1, /*voice_id*/2, /*face_id*/2, /*change_time*/1.6, /*change_face*/1, /*description*/"ハッピーハロウィン！\nにいちゃんにいたずらできて\nお菓子ももらえるなんて、ミソギ\n本当にハッピーだよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1112003, /*unit_id*/111201, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/4.8, /*change_face*/2, /*description*/"トリックオアトリート！\nお菓子をくれなきゃいたずら\nするぞ！　ミソギはお菓子\nもらってもするけどね！");
INSERT INTO `unit_comments` VALUES (/*id*/1112004, /*unit_id*/111201, /*use_type*/1, /*voice_id*/4, /*face_id*/6, /*change_time*/3.1, /*change_face*/1, /*description*/"にいちゃんはハロウィンに\nいたずらしないの？　しないなら\nミソギがにいちゃんのかわりに\nいたずらしておくね！");
INSERT INTO `unit_comments` VALUES (/*id*/1112005, /*unit_id*/111201, /*use_type*/1, /*voice_id*/5, /*face_id*/2, /*change_time*/5.1, /*change_face*/1, /*description*/"にいちゃん見て見て！\nミソギ、こんなにたくさん\nお菓子もらっちゃった！\n一緒に食べよ！");
INSERT INTO `unit_comments` VALUES (/*id*/1112006, /*unit_id*/111231, /*use_type*/1, /*voice_id*/1, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"ハッピーハロウィン！\nお菓子のお礼にとびっきりの\nいたずらをプレゼントだよ！\nちゃんと受け取ってね！");
INSERT INTO `unit_comments` VALUES (/*id*/1112007, /*unit_id*/111231, /*use_type*/1, /*voice_id*/2, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"あはははは！\nみんな驚いてる驚いてる！\nいたずらって本当に楽しいね！\nえ？　少し手加減しなきゃだめ？");
INSERT INTO `unit_comments` VALUES (/*id*/1112008, /*unit_id*/111231, /*use_type*/1, /*voice_id*/3, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"走り回ってたら\nお腹空いてきちゃった…\nもらったお菓子、少しだけ\nつまみ食いしちゃおうかな！");
INSERT INTO `unit_comments` VALUES (/*id*/1112009, /*unit_id*/111231, /*use_type*/1, /*voice_id*/4, /*face_id*/1, /*change_time*/0.0, /*change_face*/1, /*description*/"にいちゃんはいっつも\nミソギと一緒にいてくれるよね！\nにいちゃんがいるからミソギは\n安心していたずらできるんだ！");
