INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206201, /*unit_name*/"ノーティビーバー", /*prefab_id*/206201, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/650, /*atk_type*/1, /*normal_atk_cast_time*/0.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】中距離攻撃\n片っ端から木を削り倒すビーバー。\n削った木片は家作りだけでなく、\n家を守るための飛び道具となる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206300, /*unit_name*/"ルナポッド", /*prefab_id*/206300, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/1110, /*atk_type*/0, /*normal_atk_cast_time*/10.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【回復】単体ＴＰ回復\nルナの塔建造と共に造られた、エネルギー増幅器。\n漂う魔力を濃縮し、供給することで守護者を強化する。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206400, /*unit_name*/"リトルアイ", /*prefab_id*/206400, /*motion_type*/0, /*se_type*/0, /*move_speed*/270, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\n一ツ目族の王に仕える、小さな召使い。\n王様がくれた金ピカ装備が、大のお気に入り。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206500, /*unit_name*/"コマンド・ウキキー", /*prefab_id*/206500, /*motion_type*/0, /*se_type*/0, /*move_speed*/300, /*search_area_width*/100, /*atk_type*/1, /*normal_atk_cast_time*/2.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】近接攻撃\nウキキーがより成長した姿。幼少より大岩を投げ\n続けたその太腕は、絶大な打撃を繰り出す。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206700, /*unit_name*/"ヒヨリのシャドウ", /*prefab_id*/206700, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/200, /*atk_type*/1, /*normal_atk_cast_time*/2.295, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"ヒヨリの姿をした特殊な魔物\n\n・目の前の敵１キャラに物理大ダメージを与え、\n　その周りの狭い範囲の敵すべてにも\n　物理中ダメージを与える。\n・目の前の敵１キャラに物理中ダメージを\n　与える。\n・目の前の敵１キャラに物理小ダメージを与え、\n　さらに自分の物理攻撃力を中アップさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206701, /*unit_name*/"レイ（ニューイヤー）のシャドウ", /*prefab_id*/206701, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/400, /*atk_type*/1, /*normal_atk_cast_time*/1.965, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"レイ（ニューイヤー）の姿をした特殊な魔物\n\n・敵全体に物理中ダメージを与え、\n　さらにスタンさせる。\n・【挑発】敵全体を挑発する。\n　挑発後のモーション中に攻撃を受けた場合、\n　範囲内の敵すべてに物理中ダメージを与え、\n　さらに物理防御力を小ダウンさせる。\n・自分の周囲の味方すべてに\n　魔法無効バリアを展開し、さらに\n　自身の魔法防御力を中アップさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/206702, /*unit_name*/"ユイ（ニューイヤー）のシャドウ", /*prefab_id*/206702, /*motion_type*/0, /*se_type*/7, /*move_speed*/270, /*search_area_width*/600, /*atk_type*/2, /*normal_atk_cast_time*/2.27, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"ユイ（ニューイヤー）の姿をした特殊な魔物\n\n・味方全体に物理/魔法無効バリアと\n　継続ＨＰ回復状態を付与するが、\n　自身の魔法攻撃力を大ダウンさせる。\n・前から２番目の敵１キャラの\n　物理攻撃力と魔法攻撃力を大ダウンさせる。\n・魔法攻撃を行う全ての味方キャラの\n　魔法攻撃力を大アップさせる。");
