INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300100, /*unit_name*/"ミノタウロス", /*prefab_id*/300100, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】さらなる力を得た牡牛座の魔星獣。\n力の限界を超えた戦斧の一撃が全てを破砕する。\n\n・敵全体に物理大ダメージを与え、火傷状態\n　にする。\n・敵１キャラに物理大ダメージを与え、ノック\n　バックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法中ダメージを与え、\n　麻痺させる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300101, /*unit_name*/"ミノタウロス", /*prefab_id*/300101, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】さらなる力を得た牡牛座の魔星獣。\n力の限界を超えた戦斧の一撃が全てを破砕する。\n\n・敵全体に物理大ダメージを与え、火傷状態\n　にする。\n・敵１キャラに物理大ダメージを与え、ノック\n　バックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法中ダメージを与え、\n　麻痺させる。\n・自身の物理攻撃力と魔法攻撃力を大アップ\n　させ、前から１番目と２番目のキャラに\n　物理大ダメージ。\n　スキル発動後、自身の物理防御力と\n　魔法防御力を大ダウン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300200, /*unit_name*/"レサトパルト", /*prefab_id*/300200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"猛毒の尾棘で災厄を振りまく、蠍座の魔星獣。\n砂漠を護る神蟲が、信仰を失い邪神と化した。\n\n・敵全体に物理大ダメージを与える。\n　ボスがユニオンバーストを使用する毎に、\n　敵に与えるダメージがアップ。\n　この効果は、最大３回まで累積する。\n・前方範囲内の敵に物理中ダメージを与え、\n　毒状態にする。\n・自身にかかっているステータスダウン効果を\n　解除し、さらに自身の物理防御力をアップ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300201, /*unit_name*/"レサトパルト", /*prefab_id*/300201, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】信仰を失った神殿に遺る、蠍座の魔星獣。\n過去に受けた敬虔な祈りも忘れ、破滅をもたらす。\n\n・敵全体に物理大ダメージを与える。\n　このスキルを使用した後、\n　敵を攻撃するたび、以降その敵に\n　レサトパルトが与えるダメージが\n　アップする。\n　この効果は最大３回まで累積する。\n・前方1キャラを中心にした小範囲に\n　物理中ダメージを与え、毒状態にする。\n・自身にかかっているステータスダウン効果を\n　解除し、さらに自身の物理防御力をアップ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300202, /*unit_name*/"レサトパルト", /*prefab_id*/300202, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】信仰を失った神殿に遺る、蠍座の魔星獣。\n過去に受けた敬虔な祈りも忘れ、破滅をもたらす。\n\n・敵全体に物理大ダメージを与える。\n　このスキルを使用した後、\n　敵を攻撃するたび、以降その敵に\n　レサトパルトが与えるダメージが\n　アップする。\n　この効果は最大３回まで累積する。\n・前方1キャラを中心にした小範囲に\n　物理中ダメージを与え、毒状態にする。\n・自身にかかっているステータスダウン効果を\n　解除し、さらに自身の物理防御力をアップ。\n・レサトガードを１体召喚する。\n　召喚時、すでに召喚されているレサトガードを\n　１秒間無敵状態にする。\n　３体目のレサトガードが召喚された時、\n　敵全体に、キャラの最大ＨＰに応じた\n　物理割合ダメージを与える。\n　レサトガードのＨＰが０になった時、\n　レサトパルトに物理固定ダメージを与え、\n　さらに物理攻撃力を中ダウン。");
