INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300700, /*unit_name*/"オークリーダー", /*prefab_id*/300700, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】族長として群れを鼓舞し\n大地を砕くほどの連撃で\n敵に大ダメージを与える\nオーク中のオーク。\n\n・ランダムな敵を中心にした\n　3キャラに物理中ダメージを与え\n　物理防御力を上げる。\n\n・味方全体の物理攻撃力を上げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300701, /*unit_name*/"オークチーフ", /*prefab_id*/300701, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"特異な力で変質したオークリーダー。\n巨大な盾で、攻防一体の一撃を繰り出す。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方範囲内の敵の防御力を大幅に下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300800, /*unit_name*/"エルダーホーン", /*prefab_id*/300800, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ひとたび暴れ出せば\n手が付けられず、その嘶きは\n天まで届くと言われる\n伝説のワイルドホーン。\n\n・敵全体に物理中ダメージを与え、\n　小さく吹き飛ばす。\n\n・前方3キャラに物理中ダメージを\n　与え、物理攻撃力と\n　物理防御力を下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300801, /*unit_name*/"スピリットホーン", /*prefab_id*/300801, /*motion_type*/0, /*se_type*/1, /*move_speed*/270, /*search_area_width*/3000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】特異な力で変質したエルダーホーン。\n角に魔力を込め、打撃より強力な一撃を放つ。\n\n・前方範囲内の敵に魔法大ダメージを与え、\n　ノックバックさせると同時にスタンさせる。\n・前方範囲内の敵に魔法小ダメージを与え、\n　魔法防御と攻撃速度を小ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/300900, /*unit_name*/"エルダータイラント", /*prefab_id*/300900, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強力な頭突きで地を砕き\n全体に大ダメージを与える。\n身体や甲羅が巨大に進化した\n岩山のようなタートルタイラント。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301000, /*unit_name*/"シードレイク", /*prefab_id*/301000, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強力な氷のブレスで\n前衛を一瞬で氷漬けにし、\n咬みついて身体を砕く\n獰猛な深海竜。\n\n・前方3キャラに物理中ダメージを\n　与える。\n\n・前方2キャラに物理中ダメージを\n　与え、凍結状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301001, /*unit_name*/"シードレイク", /*prefab_id*/301001, /*motion_type*/0, /*se_type*/14, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】強力な氷のブレスで前衛を一瞬で\n氷漬けにし、咬みついて身体を砕く獰猛な深海竜。\n\n・前方3キャラに物理中ダメージを\n　与える。\n・前方1キャラに物理中ダメージを\n　与え、凍結状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301100, /*unit_name*/"サベージベア", /*prefab_id*/301100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】体格にそぐわぬ俊敏さと\n咬みつき攻撃で体力を奪い、\n巨大なツメで敵を引き裂く\n残虐非道な森の支配者。\n\n・前方4キャラに物理大ダメージを\n　与える。\n\n・前方1キャラに物理小ダメージを\n　与え、物理攻撃力を下げる。\n　さらに、自分の物理攻撃力を\n　上げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301101, /*unit_name*/"マッドベア", /*prefab_id*/301101, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"特異な力で変質したサベージベア。\n怒りを込めた剛腕は、敵ごと地面を引き裂く。\n\n・前方範囲内の敵に物理大ダメージを与え、\n　スタン状態にする。\n・前方の敵1キャラに物理小ダメージを与え、\n　物理攻撃力を中ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301200, /*unit_name*/"グレーターゴーレム", /*prefab_id*/301200, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】際限なくゴーレムを召喚し\n岩の針で体力と精神を同時に削る。\n無数のゴーレムの残骸を\n魔力で再構築した巨大なゴーレム。\n\n・前方4キャラに物理中ダメージを\n　与え、ＴＰを下げる。\n\n・ゴーレムを召喚する。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301201, /*unit_name*/"クリスタルゴーレム", /*prefab_id*/301201, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.3, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ルナの魔力の結晶でその身を覆う巨大ゴーレム。\n身に纏う結晶は堅牢な盾であり、全てを穿つ矛となる。\n・クリスタルゴーレムが毒、猛毒、呪い、火傷の\n　効果を受けると、行動速度が大ダウンする。\n・自身のデバフ効果をすべて解除し、\n　物理防御力と魔法防御力を大アップする。\n　さらに、自分以外の味方の物理防御力と\n　魔法防御力を中アップさせる。\n・敵全体に物理中ダメージを与え、スタンさせる。\n・前方範囲内の敵に物理大ダメージを与え、\n　味方全体の物理攻撃力と魔法攻撃力を\n　小アップさせる。\n　さらに、自身のＴＰを中回復する。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301300, /*unit_name*/"グラットン", /*prefab_id*/301300, /*motion_type*/0, /*se_type*/4, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"暴食の凶獣として悪名轟く、天秤座の魔星獣。\n奪った魂の善悪をはかり、味の違いを愉しむ。\n\n・敵からの攻撃による麻痺、スタン、束縛、\n　行動速度低下効果への耐性を持たない。\n・敵全体に魔法大ダメージを与え、さらに自\n　身の物理攻撃力と魔法攻撃力を特大アップ。\n・後方範囲内の敵に魔法中ダメージ。\n・前方範囲内の敵に魔法中ダメージを与え、Ｔ\n　Ｐを小ダウン。さらに自身のＴＰを中アップ\n　させる。");
