INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301800, /*unit_name*/"サジタリウス", /*prefab_id*/301800, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"天を駆け数多の標的を射止める射手座の魔星獣。\n雷光纏いし矢は、刹那にすべてを穿つ至高の技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与える。\n　暗闇状態の敵はダメージが3倍になる。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵1キャラに強力な暗\n　闇状態を付与。\n・前方の敵3キャラに物理ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301801, /*unit_name*/"サジタリウス", /*prefab_id*/301801, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与え、\n　魔法攻撃力を特大ダウンさせる。\n　さらに、暗闇状態の敵はダメージが増加する。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵１キャラに強力な暗\n　闇状態を付与。\n・前方の敵３キャラに物理ダメージを与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301802, /*unit_name*/"サジタリウス", /*prefab_id*/301802, /*motion_type*/0, /*se_type*/3, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】星穹の神殿を守護する射手座の魔星獣。\n万象を射抜く一閃は、紫電煌めく神域の絶技。\n\n・ユニオンバーストは敵全体に最大ＨＰに応じ\n　て魔法ダメージを与え、\n　魔法攻撃力を特大ダウンさせる。\n　さらに、暗闇状態の敵はダメージが増加する。\n・敵全体に魔法中ダメージを与え、さらに残り\n　ＨＰ割合が最も少ない敵１キャラに強力な暗\n　闇状態を付与。\n・前方の敵３キャラに物理ダメージを与える。\n・自身の矢に【黒星】を１追加し、\n　魔法攻撃力が一番高い１キャラに特大物理\n　ダメージを与え、【黒星】の数に応じて\n　追加効果を与える。\n　【黒星】の数が２の場合\n　物理防御力を大ダウンさせる。\n　【黒星】の数が３の場合\n　このスキルの威力を上げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301900, /*unit_name*/"ウェアオルカ", /*prefab_id*/301900, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.7, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】鋭い眼つきで、獲物\nと波を探し求める海のハンター。\n魔力で無限に生み出す潮水は\n敵の刃と気力を鈍らせる。\n\n・前方4キャラに物理中ダメージを\n　与え、物理攻撃力を下げる。\n\n・前方2キャラに魔法小ダメージを\n　与え、ＴＰを下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/301901, /*unit_name*/"キリングオルカ", /*prefab_id*/301901, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ルナの魔力により凶悪化した大海の狩人。\n鍛え抜いた躰で、刃と化した水流の一撃を放つ。\n\n・前方４キャラに物理大ダメージを与え、\n　物理攻撃力を中ダウンさせる。さらに、\n　味方全員のＴＰ上昇を小アップする。\n・前方小範囲に魔法中ダメージを与え、\n　ＴＰを大ダウン。\n・後ろから２番目のキャラを中心にした\n　小範囲に物理中ダメージを与え、\n　魔法攻撃力を大ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302000, /*unit_name*/"ワイルドグリフォン", /*prefab_id*/302000, /*motion_type*/0, /*se_type*/11, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】過酷な環境で進化したプチグリフォン。\n竜巻で獲物を巻き上げ、空で狩りをする。\n\n・前方範囲内の敵に魔法中ダメージ。");
