INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302200, /*unit_name*/"イクト・ガラディオン", /*prefab_id*/302200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/2, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【魔法】ルナの魔力で目覚めた、古代の機械兵器。\n無慈悲に放たれる魔力が、侵入者を阻む。\n\n・敵全体に魔法特大ダメージを与え、魔法防御力\n　を大ダウン。\n・前方範囲内の敵に物理大ダメージを与える。\n・前方範囲内の敵に魔法大ダメージを与え、\n　麻痺状態にする。");
