INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302600, /*unit_name*/"ツインピッグス", /*prefab_id*/302600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】さらなる力を得た双子座の魔星獣。\n前衛で防御しつつ、熟達した技で攻撃を行う。\n\n・敵全体に、最大ＨＰに応じた物理\n　大ダメージ。\n・後衛の敵に物理中ダメージを与え、\n　攻撃速度を小ダウン。\n・敵全体に、物理小ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302601, /*unit_name*/"ツインピッグス", /*prefab_id*/302601, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】さらなる力を得た双子座の魔星獣。\n前衛で防御しつつ、熟達した技で攻撃を行う。\n\n・敵全体に、最大ＨＰに応じた物理\n　大ダメージ。\n・後衛の敵に物理中ダメージを与え、\n　攻撃速度を小ダウン。\n・敵全体に、物理小ダメージ。\n・暴食中、2体とも自分の物理防御力と\n　魔法防御力を極大ダウン。\n　その後自分のＴＰ上昇とクリティカル率を\n　極大アップさせ、敵全体に物理大ダメージを\n　与える。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302700, /*unit_name*/"カルキノス", /*prefab_id*/302700, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】船墓の入江に巣食う、蟹座の魔星獣。\n巨大なハサミの一撃で、無数の船を沈める。\n\n・敵全体に魔法大ダメージを与え、物理防御力\n　をダウン。\n・前方範囲内の敵すべてに物理中ダメージを与\n　え、さらにスタン状態にする。\n・敵全体に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302701, /*unit_name*/"カルキノス", /*prefab_id*/302701, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】幾多の船を沈めた蟹座の魔星獣。\n大海の覇者は万物を断裁し、圧潰し、殲滅する。\n\n・敵全体に魔法大ダメージを与え、物理防御力\n　をダウン。\n・前方範囲内の敵すべてに物理中ダメージを与\n　え、さらにスタン状態にする。\n・敵全体に物理中ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302702, /*unit_name*/"カルキノス", /*prefab_id*/302702, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】幾多の船を沈めた蟹座の魔星獣。\n大海の覇者は万物を断裁し、圧潰し、殲滅する。\n\n・敵全体に魔法大ダメージを与え、物理防御力\n　をダウン。\n・前方範囲内の敵すべてに物理中ダメージを与\n　え、さらにスタン状態にする。\n・敵全体に物理中ダメージ。\n・一定時間姿を変えて、自分の魔法攻撃力を\n　特大アップさせ、全てのスキルを魔法攻撃に\n　変化させる。\n　その間、自分の物理防御力と魔法防御力を\n　特大ダウンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302800, /*unit_name*/"オルレオン", /*prefab_id*/302800, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"サバンナの覇者と名高い、獅子座の魔星獣。\n刃向かう者は、灼熱の咆哮で焼き尽くす。\n\n・敵全体に、最大ＨＰに応じた防御力無視の\n　物理大ダメージを与え、残りＨＰの少ない\n　敵はさらに火傷状態にする。\n・敵1キャラに物理大ダメージを与え、火傷\n　状態の場合はさらに追加ダメージ。\n・前方範囲内の敵に物理中ダメージを与え、\n　火傷状態の場合はさらに追加ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302801, /*unit_name*/"オルレオン", /*prefab_id*/302801, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】荒涼たる原野に君臨する獅子座の魔星獣。\n鋭爪で大気を切裂き、業火の咆哮が大地を焦がす。\n\n・敵全体に、最大ＨＰに応じた防御力無視の\n　物理大ダメージを与え、残りＨＰの少ない\n　敵はさらに火傷状態にする。\n・敵1キャラに物理大ダメージを与え、火傷\n　状態の場合はさらに追加ダメージ。\n・前方範囲内の敵に物理中ダメージを与え、\n　火傷状態の場合はさらに追加ダメージ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302802, /*unit_name*/"オルレオン", /*prefab_id*/302802, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.0, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】荒涼たる原野に君臨する獅子座の魔星獣。\n鋭爪で大気を切裂き、業火の咆哮が大地を焦がす。\n\n・敵全体に、最大ＨＰに応じた防御力無視の\n　物理大ダメージを与え、残りＨＰの少ない\n　敵はさらに火傷状態にする。\n・敵1キャラに物理大ダメージを与え、火傷\n　状態の場合はさらに追加ダメージ。\n・前方範囲内の敵に物理中ダメージを与え、\n　火傷状態の場合はさらに追加ダメージ。\n・敵全体に、最大ＨＰに応じた防御力無視の\n　魔法中ダメージを与え、スタンさせる。\n　残りＨＰの少ない敵は大幅にスタンさせる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/302900, /*unit_name*/"アクアリオス", /*prefab_id*/302900, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"神瓶に宿る魔力に取込まれた、水瓶座の魔星獣。\n強大な力に溺れ、大海を支配せんと暴れまわる。\n\n・ユニオンバーストは、敵全体に魔法小ダメー\n　ジを計６回与える。\n　キャラのＨＰが８０％を切るとノックバック\n　させ、４０％を切るとさらに追加でノックバ\n　ックとスタン状態を付与。\n・目の前の敵３キャラに物理中ダメージ。\n・自身のステータスダウン効果と、敵全体のス\n　テータスアップ効果を打消し、自身のＴＰを\n　アップ。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303000, /*unit_name*/"トルペドン", /*prefab_id*/303000, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"大海原を爆走し、鬼シケ呼び込む魚座の魔星獣。\n取巻く手下の赤ビレはカチコミ上等ヘッドの証。\n\n・物理攻撃力アップ・ダウン効果の影響を受け\n　やすい。\n・ユニオンバーストは、敵全体に物理大ダメー\n　ジを与え、自分の行動速度と物理攻撃力をア\n　ップ。\n・敵全体に物理小ダメージ。\n・前方２キャラに物理中ダメージを与え、物理\n　防御力を小ダウン。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303100, /*unit_name*/"トウミングマ", /*prefab_id*/303100, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】氷穴最奥に引き篭り\n生涯冬眠すると決めた巨大熊。\n長い歳月を夢うつつの中で過ごし\n獲物を狩るその一瞬、覚醒する。\n\n・敵全体に魔法大ダメージを与え、\n　物理攻撃力を下げる。\n\n・前方3キャラに魔法小ダメージを\n　与える。さらに、前方1キャラを\n　大きく吹き飛ばし、氷結状態\n　にして移動速度を下げる。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303200, /*unit_name*/"マダムピック", /*prefab_id*/303200, /*motion_type*/0, /*se_type*/6, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.6, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】吹雪と共に現れ、\n縄張りを荒らす者には\n大群を呼び寄せ襲い掛かる\n雪国イチのゴージャスマダム。\n\n・前方4キャラに魔法中ダメージを\n　与え、物理攻撃力を下げる。\n\n・ランダムな2キャラに魔法\n　小ダメージを与え、\n　凍結状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/303300, /*unit_name*/"ガーゴイル", /*prefab_id*/303300, /*motion_type*/0, /*se_type*/13, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/2.1, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】朽ちた遺跡に残り\n主亡き地を護る、謎の鳥像。\n街も命令も、全て砂と消えた今は\n誰彼構わず炎を吐き襲い掛かる。\n\n・前方5キャラに魔法中ダメージを\n　与え、火傷状態にする。さらに、\n　魔法防御力を下げる。\n\n・前方3キャラに魔法小ダメージを\n　与え、暗闇状態にする。");
