INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306100, /*unit_name*/"ジャッカルシーフ", /*prefab_id*/306100, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】闇に紛れ、人里で盗みを\nはたらく魔物の頭目。\nくすねたエモノを容赦なく投げつけ\n開拓の邪魔をするならず者。\n\n・前方3キャラに物理中ダメージを\n　与え、麻痺させる。\n\n・敵全体に物理小ダメージを与え、\n　物理防御力と行動速度を下げる。\n　さらに、前方小範囲を毒状態\n　にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306200, /*unit_name*/"マスター・センリ", /*prefab_id*/306200, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】森奥に座し、周辺一帯を\n手下と共に支配する老獪な大狸。\nありもしない幻を見せ、精神を蝕む\n幻影攻撃を巧みに操る。\n\n・敵全体に魔法中ダメージを与え、\n　ワッパダヌキ２体を召喚する。\n\n・敵全体に魔法小ダメージを与え、\n　無敵状態でない召喚モンスターを\n　消滅させる。\n　さらに敵のＨＰが40％以下の\n　場合、麻痺状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306300, /*unit_name*/"マスター・ウキキー", /*prefab_id*/306300, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ルナの魔力を使いウキキーを統べる仙猿。\n宝武具の棍を自在に操り、すべての敵をなぎ払う。\n\n・敵全体に物理大ダメージを与える。\n　この攻撃は火傷状態の敵に対して、\n　ダメージが1.8倍になる。\n・前方中範囲の敵に物理中ダメージを与える。\n・前方小範囲の敵に魔法中ダメージを与え、\n　火傷状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306301, /*unit_name*/"マスター・ウキキー", /*prefab_id*/306301, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】ルナの魔力を使いウキキーを統べる仙猿。\n宝武具の棍を自在に操り、すべての敵をなぎ払う。\n\n・敵全体に物理大ダメージを与え、\n　さらに物理攻撃キャラの物理攻撃力を下げる。\n　この攻撃は火傷状態の敵に対して、\n　ダメージが1.8倍になる。\n・前方中範囲の敵に物理中ダメージを与える。\n・前方小範囲の敵に魔法中ダメージを与え、\n　火傷状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306400, /*unit_name*/"ワイルドクロウ", /*prefab_id*/306400, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/3.5, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】荒れ山の捕食者として君臨\nする、凶悪な怪鳥獣。\n狩りを続けることでつくりあげた\n膂力と剛爪が、侵入者をつけ狙う。\n\n・敵全体に物理大ダメージを与え、\n　ＴＰ上昇をダウンさせる。\n　発動するたびにダメージがアップ\n　する。この効果は最大３回まで\n　累積する。\n\n・後ろから２番目の敵を中心にした\n　小範囲に物理小ダメージを与え、\n　麻痺状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306500, /*unit_name*/"トライバルオーク", /*prefab_id*/306500, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/0.9, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】辺境の森のオークたちの\n首魁。独自の民族性の奇怪な\nリズムを撒き散らす。その怪音は、\n敵対者を混迷の渦に突き落とす。\n\n・敵全体に物理大ダメージを与え、\n　物理防御力と魔法防御力をダウン\n　さらに自身の物理攻撃力と\n　魔法攻撃力をアップする。\n\n・前方４キャラに魔法小ダメージを\n　与え、敵のＨＰが70％以下の\n　場合、混乱状態にする。");
INSERT INTO `unit_enemy_data` VALUES (/*unit_id*/306600, /*unit_name*/"ベノムサラマンドラ", /*prefab_id*/306600, /*motion_type*/0, /*se_type*/16, /*move_speed*/270, /*search_area_width*/1000, /*atk_type*/1, /*normal_atk_cast_time*/1.8, /*cutin*/0, /*visual_change_flag*/0, /*comment*/"【物理】獲物を求め水辺を猛毒\nで満たす、サンショウウオの魔物。\n漏れるほどに溜め込んだ猛毒は、\n近づくだけで意識を奪う危険物。\n\n・敵全体に魔法中ダメージを与え、\n　毒状態にする。\n　さらに猛毒状態にする。\n\n・前方範囲内の敵に\n　魔法小ダメージを与え、\n　猛毒状態にする。");
